00978   x = 0; 
00979   y = 0;
00980 
00981   if(!bitmapref)
00982     {
00983       return false;
00984     }
00985   
00986   GRPBITMAP* _bitmap = bitmapref->ConvertToMode(bitmapscreen->GetMode());
00987   if(!_bitmap)
00988     {
00989       return false;
00990     }
00991 
00992   if(_bitmap->GetMode() != bitmapscreen->GetMode()) 
00993     {
00994       return false;
00995     }
00996 
00997   if(!_bitmap->GetBuffer())
00998     {
00999       return false;
01000     }
01001   
01002   XDWORD*   bufferscreen        = (XDWORD*)bitmapscreen->GetBuffer();
01003   XDWORD*   bufferbitmap        = (XDWORD*)_bitmap->GetBuffer();
01004   XDWORD    sizepixel           = sizeof(XDWORD);
01005   XDWORD    bufferscreensize    = (bitmapscreen->GetBufferSize() / sizepixel) - _bitmap->GetWidth();
01006   XDWORD    bufferbmplinesize   = _bitmap->GetWidth();
01007   XDWORD    ndiff               = 0;
01008   bool      found               = false;
01009   
01010   for(XDWORD scrpos = 0; scrpos < bufferscreensize; scrpos++)
01011     {
01012       ndiff = 0;
01013       for(XDWORD bmppos = 0; bmppos < bufferbmplinesize; bmppos++)  
01014         {    
01015           if(bufferscreen[scrpos + bmppos] != bufferbitmap[bmppos])
01016             {
01017               if(!IsSimilarPixel(bufferscreen[scrpos + bmppos], bufferbitmap[bmppos], pixelmargin))
01018                 {
01019                   ndiff++;
01020                 }
01021                else
01022                 {
01023                   //FillLineDebug(bitmapscreen, bufferscreen, scrpos + bmppos, 1, 0xFF0000FF);
01024                 } 
01025             }
01026         }
01027                
01028       found = DifferencesPerCent(ndiff, bufferbmplinesize, difflimitpercent);
01029       if(found)
01030         {
01031           // FillLineDebug(bitmapscreen, bufferscreen, scrpos, bufferbmplinesize, 0xFF0000FF);
01032           
01033           found = false;
01034 
01035           XDWORD srcpixelsline  = bitmapscreen->GetWidth();
01036           XDWORD scrpos_tmp     = scrpos;
01037           XDWORD bmppos_tmp     = bufferbmplinesize;
01038          
01039           x =  (scrpos % bitmapscreen->GetWidth());
01040           y =  bitmapscreen->GetHeight() - (scrpos / srcpixelsline) - _bitmap->GetHeight();           
01041 
01042           scrpos_tmp += srcpixelsline;              
01043     
01044           for(XDWORD line = 1; line < _bitmap->GetHeight(); line++)
01045             {                                                   
01046               ndiff = 0;
01047               for(XDWORD bmppos = 0; bmppos < bufferbmplinesize; bmppos++)  
01048                 {    
01049                   if(scrpos_tmp + bmppos >= bufferscreensize)
01050                     {
01051                       ndiff += difflimitpercent;       
01052                       break;
01053                     }
01054 
01055                   if(bufferscreen[scrpos_tmp + bmppos] != bufferbitmap[bmppos_tmp])
01056                     {
01057                       if(!IsSimilarPixel(bufferscreen[scrpos_tmp + bmppos], bufferbitmap[bmppos_tmp], pixelmargin))
01058                         {
01059                           ndiff++;
01060                         }
01061                        else
01062                         {
01063                           // FillLineDebug(bitmapscreen, bufferscreen, scrpos_tmp + bmppos, 1, 0xFF0000FF);
01064                         } 
01065                     }  
01066                   
01067                   bmppos_tmp++;
01068                 }
01069               
01070               found = DifferencesPerCent(ndiff, bufferbmplinesize, difflimitpercent);              
01071 
01072               if(!found)
01073                 {
01074                   break;              
01075                 }
01076 
01077               //FillLineDebug(bitmapscreen, bufferscreen, scrpos_tmp, bufferbmplinesize, 0xFF0000FF);
01078 
01079               scrpos_tmp += srcpixelsline;
01080 
01081             }
01082 
01083           if(found)                  
01084             {
01085               break;
01086             }              
01087         }  
01088     } 
01089   
01090   if(!found)
01091     {
01092       x = 0;
01093       y = 0;
01094     }
01095    else
01096     {
01097       GRPVIEWPORT* viewport = NULL;
01098       GRPCANVAS*   canvas   = NULL;
01099       GRPSCREEN*   screen   = SCRIPT_LIB_WINDOW::GetAppGraphics()->GetMainScreen();
01100 
01101       if(screen) viewport = screen->GetViewport(0);
01102       if(viewport) canvas = viewport->GetCanvas();
01103 
01104       if(canvas)
01105         {
01106           GRP2DCOLOR_RGBA8  colorred(255, 0, 0);
01107 
01108           canvas->SetLineWidth(1.0f);
01109           canvas->SetLineColor(&colorred);
01110 
01111           canvas->Rectangle(x, y, x + _bitmap->GetWidth(), y + _bitmap->GetHeight());  
01112           canvas->PutPixel((x + _bitmap->GetWidth()/2), y + (_bitmap->GetHeight()/2), &colorred);
01113 
01114           screen->UpdateViewports();
01115         }      
01116     }
01117  
01118   delete _bitmap;
01119   
01120   return found;  
01121 }
