01137 bool PutBitmap(int x, int y, GRPBITMAP* bitmap)
01138 {
01139   if(!SCRIPT_LIB_WINDOW::GetAppGraphics())
01140     {
01141       return false;
01142     }
01143                                         
01144   GRPVIEWPORT* viewport = NULL;
01145   GRPCANVAS*   canvas   = NULL;
01146   GRPSCREEN*   screen   = SCRIPT_LIB_WINDOW::GetAppGraphics()->GetMainScreen();
01147 
01148   if(screen) viewport = screen->GetViewport(0);
01149   if(viewport) canvas = viewport->GetCanvas();
01150 
01151   if(canvas)
01152     {
01153       canvas->PutBitmap(x, y, bitmap);  
01154       screen->UpdateViewports();                                              
01155     }
01156   
01157   return true;
01158 }
