01215 void FillLineDebug(GRPBITMAP* bitmapscreen, XDWORD*  bufferscreen, XDWORD scrpos, XDWORD linesize, XDWORD color)
01216 {
01217   for(XDWORD c=scrpos; c<(scrpos + linesize); c++)
01218     {
01219       bufferscreen[c] = color; // 0xFF0000FF;
01220     }
01221 
01222   PutBitmap(0, 0, bitmapscreen);
01223 }
