00782 bool UI_ELEMENT::SwicthStateBlink()
00783 {
00784   if(!blink_nchanges)
00785     {
00786       blink_state = true;
00787       blink_nchanges++;
00788 
00789       return true;
00790     }
00791 
00792   blink_state = !blink_state;  
00793 
00794   blink_nchanges++;
00795 
00796   blink_xtimer->Reset();
00797 
00798   return true;
