00176 bool UI_MANAGER::Load(XPATH& pathfile)
00177 {
00178   bool status = false;  
00179 
00180   if(pathfile.Find(__L(".zip"), true) != XSTRING_NOTFOUND) 
00181     {
00182       iszippedfile = true;
00183     }
00184 
00185   if(!iszippedfile)
00186     {
00187       status = LoadLayout(pathfile);
00188     }
00189    else
00190     {
00191       unzipfile = new XFILEUNZIP();
00192       if(!unzipfile) return false;
00193 
00194       if(unzipfile->Open(pathfile))
00195         {
00196           XSTRING origin_drive;
00197           XPATH   origin_path;
00198           XSTRING origin_namefile;
00199           XSTRING origin_ext;
00200           
00201           pathfile.GetDrive(origin_drive);
00202           pathfile.GetPath(origin_path);
00203           pathfile.GetNamefile(origin_namefile);
00204           pathfile.GetExt(origin_ext);
00205                           
00206           unzippathfile  = origin_drive;
00207           unzippathfile += origin_path; 
00208 
00209           XSTRING namefile;
00210 
00211           namefile    = origin_namefile;
00212           namefile   += __L(".xml");
00213                   
00214           status = unzipfile->DecompressFile(namefile, unzippathfile, namefile.Get());
00215           if(status)
00216             {
00217               XPATH unzippathfile_tmp;
00218 
00219               unzippathfile_tmp  = unzippathfile;
00220               unzippathfile_tmp += namefile;
00221 
00222               status = LoadLayout(unzippathfile_tmp);                
00223 
00224               DeleteTemporalUnZipFile(unzippathfile_tmp);                          
00225             }
00226         }
00227        else 
00228         {
00229           CloseUnZipFile();        
00230         }        
00231     }
00232 
00233   return status;
00234 }
