00626 bool UI_MANAGER::Skin_CreateAll(GRPSCREEN* screen, GRPCONTEXT* context)
00627 {  
00628   //--------------------------------------------------------------------------
00629   // Canvas Skins 
00630   
00631   if(!screen) return false;
00632     
00633   UI_SKINCANVAS* skincanvas = NULL;
00634 
00635   for(int c=0; c<2; c++)
00636     {
00637       switch(c)
00638         {
00639           case  0 : skincanvas =  new UI_SKINCANVAS(screen);        break;  
00640           case  1 : skincanvas =  new UI_SKINCANVAS_FLAT(screen);   break;  
00641         }    
00642 
00643       if(skincanvas)
00644         {
00645           Skin_Add(skincanvas);
00646           skincanvas = NULL;
00647         }
00648     }  
00649 
00650   //--------------------------------------------------------------------------
00651   // Context Skins 
00652   
00653   return true;
00654 }
