00792 bool UI_MANAGER::Update()
00793 {
00794   UI_LAYOUT* layout = Layouts_Get(selected_layout);
00795   if(!layout) return false;
00796 
00797   bool status;
00798 
00799   status = layout->Update();  
00800   if(status)
00801     {         
00802       ChangeTextElementValue(layout);    
00803 
00804       if(inall_layout != UI_MANAGER_LAYOUT_NOTSELECTED)
00805         {         
00806           layout = Layouts_Get(inall_layout);
00807           if(!layout) return false;          
00808                     
00809           ChangeTextElementValue(layout);   
00810   
00811           status = layout->Update(); 
00812           if(status) ChangeTextElementValue(layout);                  
00813         }
00814 
00815       if(element_modal)
00816         {
00817           if(xmutex_modal) xmutex_modal->Lock(); 
00818           
00819           Elements_SetToRedraw(element_modal);
00820         
00821           if(xmutex_modal) xmutex_modal->UnLock(); 
00822         }
00823     }
00824 
00825   if(virtualkeyboard) 
00826     {
00827       if(virtualkeyboard->IsShow())
00828         {
00829           Elements_SetToRedraw(virtualkeyboard->GetElementEditable());
00830         }
00831     }
00832 
00833   return status;
00834 }
