00866 bool UI_MANAGER::Background_PutColor()
00867 {
00868   UI_SKIN* skin   = GEN_USERINTERFACE.Skin_Selected();
00869   bool     status = false;
00870 
00871   if(GEN_USERINTERFACE.Skin_Selected())
00872     {
00873       switch(skin->GetDrawMode())
00874           {
00875             case UI_SKIN_DRAWMODE_UNKNOWN  :  break;
00876 
00877             case UI_SKIN_DRAWMODE_CANVAS   :  { UI_SKINCANVAS* skin_canvas  = (UI_SKINCANVAS*)GEN_USERINTERFACE.Skin_Selected();
00878                                                 if(!skin->Background_GetColor()->IsEmpty()) 
00879                                                   { 
00880                                                     GRPSCREEN* screen = skin_canvas->GetScreen();      
00881                                                     GRPCANVAS* canvas = skin_canvas->GetCanvas();    
00882                                                     if(canvas && screen) 
00883                                                       {
00884                                                         UI_COLOR color;
00885 
00886                                                         color.SetFromString(skin->Background_GetColor()->Get());   
00887 
00888                                                         GRP2DCOLOR_RGBA8 color_canvas(color.GetRed(), color.GetGreen(), color.GetBlue(), color.GetAlpha());  
00889                                                                                                                     
00890                                                         canvas->Clear(&color_canvas);
00891 
00892                                                         status = true;                                                        
00893                                                       }
00894                                                   }
00895                                               }
00896                                               break;
00897 
00898             case UI_SKIN_DRAWMODE_CONTEXT  :  break;
00899           }
00900      }
00901                       
00902   return status;
00903 }
