01064 UI_LAYOUT* UI_MANAGER::GetElementLayout(UI_ELEMENT* element)
01065 {
01066   if(!element) return NULL;
01067 
01068   if(layouts.IsEmpty()) return NULL;
01069 
01070   for(XDWORD c=0; c<layouts.GetSize(); c++)
01071     { 
01072       UI_LAYOUT* layout = layouts.Get(c);
01073       if(layout) 
01074         { 
01075           XVECTOR<UI_ELEMENT*>* layout_elements = layout->Elements_Get();
01076           if(layout_elements) 
01077             {
01078               bool    found = false;
01079               XDWORD  index = 0;
01080  
01081               do{ UI_ELEMENT* _element = layout_elements->Get(index);
01082                   if(_element) 
01083                     {
01084                       if(_element == element) 
01085                         {
01086                           found = true;
01087                           break;
01088                         }
01089                     } 
01090 
01091                   index++;
01092 
01093                 } while(index < layout_elements->GetSize());    
01094                                
01095               if(found) return layout;              
01096             }
01097         }
01098     }
01099 
01100   return NULL;
01101 }
