01165 bool UI_MANAGER::PutElementLastPositionLayout(UI_ELEMENT* element)
01166 {
01167   if(!element) return false;
01168 
01169   UI_LAYOUT* layout = GetElementLayout(element);
01170   if(!layout) return false;
01171 
01172   XVECTOR<UI_ELEMENT*>* layout_elements = layout->Elements_Get();
01173   if(!layout_elements) return false;
01174   
01175   bool   found = false;
01176   XDWORD index = 0;
01177 
01178   do{ UI_ELEMENT* _element = layout_elements->Get(index);
01179       if(_element) 
01180         {
01181           if(_element == element) 
01182             {
01183               found = true;
01184               break;
01185             }
01186         } 
01187 
01188        index++;
01189 
01190     } while(index < layout_elements->GetSize());
01191 
01192   if(!found) return false;  
01193 
01194   UI_ELEMENT* sustitute_element =  layout_elements->Get(layout_elements->GetSize()-1);
01195   if(!sustitute_element) return false;
01196 
01197   layout_elements->Set(layout_elements->GetSize()-1, layout_elements->Get(index));
01198   layout_elements->Set(index, sustitute_element);         
01199 
01200   return true;
01201 }
