01444 
01458 int UI_MANAGER::GetOutputTextChangeID(XSTRING* text, int start, XSTRING& value)
01459 {
01460   if(!text) return false;
01461   
01462   int ini_mask;
01463   int end_mask;
01464 
01465   ini_mask = text->Find(UI_MANAGER_LAYOUT_TEXT_INI, true, start);
01466   if(ini_mask != XSTRING_NOTFOUND)
01467     {
01468       end_mask = text->Find(UI_MANAGER_LAYOUT_TEXT_END, true, ini_mask);
01469       if(end_mask != XSTRING_NOTFOUND)
01470         {
01471           text->Copy(ini_mask+2, end_mask, value);
01472           return ini_mask;
01473         }       
01474     }                             
01475 
01476   return XSTRING_NOTFOUND;
01477 }
