01492 UI_ANIMATION* UI_MANAGER::GetOrAddAnimationCache(XCHAR* name, XCHAR* resource)
01493 {
01494   UI_ANIMATION* animation = NULL;
01495 
01496   animation = GEN_UI_ANIMATIONS.Get(name);
01497   if(animation) return animation;
01498 
01499   animation = GEN_UI_ANIMATIONS.Get(resource);
01500   if(animation) return animation;
01501 
01502   animation = new UI_ANIMATION();
01503   if(!animation) return NULL;
01504   
01505   XSTRING resourcename;
01506   bool    status = false;
01507 
01508   resourcename = resource;
01509 
01510   switch(skin_selected->GetDrawMode())
01511     {
01512       case UI_SKIN_DRAWMODE_UNKNOWN   : break;
01513 
01514       case UI_SKIN_DRAWMODE_CANVAS    : { UI_SKINCANVAS* skincanvas = (UI_SKINCANVAS*)skin_selected; 
01515                                           if(skincanvas) 
01516                                             {
01517                                               status = animation->LoadFromFile(resourcename, skincanvas->GetCanvas()->GetMode());                                                                           
01518                                             }
01519                                         }
01520                                         break;
01521 
01522       case UI_SKIN_DRAWMODE_CONTEXT   : break;
01523     } 
01524 
01525   if(status)   
01526     {      
01527       animation->GetName()->Set(name);  
01528       animation->GetResource()->Set(resource);
01529 
01530       GEN_UI_ANIMATIONS.Add(animation);
01531 
01532       return animation;
01533     }
01534   
01535   delete animation;
01536 
01537   return NULL;  
01538 }
