01605 
01618 bool UI_MANAGER::SendEvent(UI_XEVENT_TYPE event, ...)
01619 {
01620   UI_XEVENT xevent(this, event);
01621   va_list   arg;
01622   bool      postevent = false;
01623   bool      status    = false;
01624 
01625   if(xmutex_UIevent) xmutex_UIevent->Lock();
01626 
01627   va_start(arg, event);
01628   
01629   switch(event)
01630     {
01631       case UI_XEVENT_TYPE_INPUT_CURSOR_MOVE         : 
01632       case UI_XEVENT_TYPE_INPUT_SELECCTION          : { int x = (int)va_arg(arg, int);
01633                                                         int y = (int)va_arg(arg, int);
01634 
01635                                                         xevent.SetXPos((XDWORD)x);
01636                                                         xevent.SetYPos((XDWORD)y);
01637 
01638                                                         postevent = true;
01639                                                       }
01640                                                       break;
01641 
01642       case UI_XEVENT_TYPE_INPUT_CURSOR_MOTION       : { void* ptr = (void*)va_arg(arg, void*);
01643                                                         if(ptr)
01644                                                           {
01645                                                             INPCURSORMOTION* cursormotion = (INPCURSORMOTION*)ptr;
01646                                                             if(cursormotion)
01647                                                               {                  
01648                                                                 xevent.SetCursorMotion(cursormotion);
01649                                                                 postevent = true;
01650                                                               }
01651                                                           }  
01652                                                       }
01653                                                       break;  
01654 
01655       case UI_XEVENT_TYPE_OUTPUT_SELECTED           : { void* ptr = (void*)va_arg(arg, void*);
01656                                                         if(ptr)
01657                                                           {   
01658                                                             UI_ELEMENT* element = (UI_ELEMENT*)ptr;
01659                                                             if(element) 
01660                                                               {
01661                                                                 UI_ELEMENT* element_father = element->GetFather();
01662                                                         
01663                                                                 if(element_father) 
01664                                                                   {
01665                                                                     if(!element_father->IsVisible()) break;
01666                                                                   }
01667 
01668                                                                 if(element->IsVisible()) 
01669                                                                   { 
01670                                                                     //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("UI Element [%s]: Selected. "), element->GetName()->Get());
01671 
01672                                                                     xevent.SetElement(element);          
01673                                                                     postevent = true;
01674                                                                   }
01675                                                               }
01676                                                           }
01677                                                       }
01678                                                       break;
01679 
01680       case UI_XEVENT_TYPE_OUTPUT_TEXTTOCHANGE       : { void* ptr = (void*)va_arg(arg, void*);
01681                                                         if(ptr)
01682                                                           {   
01683                                                             UI_ELEMENT* element = (UI_ELEMENT*)ptr;
01684                                                             if(element) 
01685                                                               {
01686                                                                 XSTRING* maskvalue = (XSTRING*)va_arg(arg, void*);
01687                                                                 if(maskvalue)  xevent.GetMaskTextValue()->Set(maskvalue->Get());
01688 
01689                                                                 /*
01690                                                                 ChangeAutomaticLiteralText((UI_ELEMENT_TEXT*)element, maskvalue, xevent.GetMaskText());
01691                                                                 
01692                                                                 if(!xevent.GetMaskText()->IsEmpty())
01693                                                                   {
01694                                                                     XSTRING* maskresolve = (XSTRING*)va_arg(arg, void*);
01695                                                                     if(maskresolve) maskresolve->Set(xevent.GetMaskText()->Get()); 
01696                                                                     postevent = false;
01697                                                                   }
01698                                                                  else   
01699                                                                  */
01700                                                                   {
01701                                                                     xevent.SetElement(element);  
01702                                                                     postevent = true;    
01703                                                                   }
01704                                                               }
01705                                                           }
01706                                                       }  
01707                                                       break;
01708 
01709        case UI_XEVENT_TYPE_OUTPUT_CHANGECONTENTS    : { void* ptr = (void*)va_arg(arg, void*);
01710                                                         if(ptr)
01711                                                           {   
01712                                                             UI_ELEMENT* element = (UI_ELEMENT*)ptr;
01713                                                             if(element) 
01714                                                               {
01715                                                                 xevent.SetElement(element);                                                                        
01716                                                                 postevent = true;
01717                                                               }  
01718                                                           }
01719                                                       }
01720                                                       break;
01721 
01722 
01723 
01724     }
01725   
01726   if(postevent) 
01727     {          
01728       status = PostEvent(&xevent);         
01729       if(status)
01730         {  
01731           switch(event)
01732             {
01733               case UI_XEVENT_TYPE_OUTPUT_TEXTTOCHANGE   : { XSTRING* maskresolve = (XSTRING*)va_arg(arg, void*);
01734                                                             if(maskresolve) maskresolve->Set(xevent.GetMaskTextResolved()->Get()); 
01735                                                           }  
01736                                                           break;
01737             }
01738         }  
01739     }
01740 
01741   va_end(arg);
01742 
01743   if(xmutex_UIevent) xmutex_UIevent->UnLock();
01744 
01745   return status;
01746 
01747 }
01748 
01749 
01763 bool UI_MANAGER::ChangeAutomaticTextElementValue(UI_ELEMENT* element, XSTRING* maskvalue, XSTRING* maskresolved)
01764 {
01765   if(!element)        return false;
01766   if(!maskvalue)      return false;
01767   if(!maskresolved)   return false;
01768 
01769   UI_ELEMENT* father = element->GetFather();
01770   if(father)
01771     {
01772       if(father->GetType() == UI_ELEMENT_TYPE_PROGRESSBAR)
01773         {
01774           if(!maskvalue->Compare(__L("PROGRESSBAR_PERCENT"), true))
01775             {
01776               UI_ELEMENT_PROGRESSBAR* element_progressbar = (UI_ELEMENT_PROGRESSBAR*)father;
01777               static float last_level = -10;
01778         
01779               if(last_level != element_progressbar->GetLevel())                
01780                 {              
01781                   maskresolved->Format(__L("%d"), (int)element_progressbar->GetLevel());
01782                   maskresolved->Add(__L("\x25"));
01783 
01784                   last_level = element_progressbar->GetLevel();                                  
01785 
01786                   return true;
01787                 }              
01788             }
01789         }
01790     }
01791 
01792   return false;
01793 }
01794 
01795 
01809 bool UI_MANAGER::ChangeTextElementValue(UI_ELEMENT* element, XSTRING* text, XSTRING& value)
01810 {
01811   XSTRING  maskvalue;
01812   XSTRING  maskresolve;  
01813   int      start      = 0;
01814   int      new_start  = 0;
01815   bool     change     = false;
01816   
01817   do{ new_start = GEN_USERINTERFACE.GetOutputTextChangeID(text, start, maskvalue);
01818 
01819       if(new_start != XSTRING_NOTFOUND)
01820         {
01821           XSTRING subtext;
01822        
01823           text->Copy(start, new_start, subtext);
01824           if(!subtext.IsEmpty()) value.Add(subtext);
01825            
01826           maskresolve.Empty();
01827 
01828           if(ChangeAutomaticTextElementValue(element, &maskvalue, &maskresolve))
01829             {                
01830               value.Add(maskresolve);                 
01831               change = true;                    
01832             }
01833            else 
01834             {
01835               SendEvent(UI_XEVENT_TYPE_OUTPUT_TEXTTOCHANGE, element, &maskvalue, &maskresolve);
01836               if(!maskresolve.IsEmpty()) 
01837                 {
01838                   value.Add(maskresolve);                 
01839                   change = true;              
01840                 }  
01841             }
01842 
01843           start = new_start + maskvalue.GetSize() + 3;               
01844 
01845         }
01846        else 
01847         {
01848           XSTRING subtext;
01849 
01850           text->Copy(start, subtext);
01851           value.Add(subtext);                                  
01852         }
01853 
01854     } while(new_start != XSTRING_NOTFOUND);
01855 
01856   return change;
01857 }
01858 
01859 
01872 bool UI_MANAGER::ChangeTextElementValue(UI_ELEMENT* element, UI_SKIN* skin)
01873 {
01874   if(!element) return false;
01875 
01876   bool change = false;
01877 
01878   switch(element->GetType())
01879     {
01880       case  UI_ELEMENT_TYPE_TEXT      : { UI_ELEMENT_TEXT*  element_text = (UI_ELEMENT_TEXT*)element;
01881                                           XSTRING           resolve;
01882 
01883                                           if(element_text->GetMaskText()->IsEmpty())    return false;
01884                                                                                                                          
01885                                           change = ChangeTextElementValue(element_text, element_text->GetMaskText(), resolve);   
01886                                                                                                             
01887                                           if(change)
01888                                             {                                                                                                                                                                                          
01889                                               UI_SKINCANVAS* skin_selected = (UI_SKINCANVAS*)GEN_USERINTERFACE.Skin_Selected(); 
01890                                               if(skin_selected)
01891                                                 {
01892                                                   double width  = 0.0f;
01893                                                   double height = 0.0f;
01894 
01895                                                   UI_ELEMENT* father =(UI_ELEMENT_TEXT*)element->GetFather(); 
01896                                                   if(father)
01897                                                     {
01898                                                       width  = father->GetBoundaryLine()->width;
01899                                                       height = father->GetBoundaryLine()->height;
01900 
01901                                                       Elements_SetToRedraw(father);    
01902                                                     }
01903                                                    else
01904                                                     {
01905                                                       width  = skin_selected->GetCanvas()->GetWidth();
01906                                                       height = skin_selected->GetCanvas()->GetHeight();
01907                                                     }                                                              
01908                                                    
01909                                                   switch(skin_selected->GetDrawMode())
01910                                                     {
01911                                                       case UI_SKIN_DRAWMODE_UNKNOWN   : break;
01912 
01913                                                       case UI_SKIN_DRAWMODE_CANVAS    : { element->GetBoundaryLine()->height  = 0;
01914                                                                                           element->GetBoundaryLine()->width   = 0;
01915                                                                                          
01916                                                                                           if(element_text->GetText()->Compare(resolve.Get(), true))  
01917                                                                                             { 
01918                                                                                               element_text->GetText()->Set(resolve);   
01919                                                                                               
01920                                                                                               skin_selected->CalculeBoundaryLine_AllElements(element, false);
01921                                                                                               skin_selected->CalculePosition(element, width, height);                                                                                              
01922 
01923                                                                                               Elements_SetToRedraw(element);    
01924                                                                                             }
01925                                                                                         }
01926                                                                                         break;
01927 
01928                                                       case UI_SKIN_DRAWMODE_CONTEXT   : break;
01929                                                     }                                                                                                             
01930                                                 }                                                
01931                                             }
01932                                         }
01933                                         break;
01934 
01935       case UI_ELEMENT_TYPE_TEXTBOX    : { UI_ELEMENT_TEXTBOX* element_textbox = (UI_ELEMENT_TEXTBOX*)element;
01936                                           XSTRING             resolve;
01937 
01938                                           if(element_textbox->GetMaskText()->IsEmpty()) return false;
01939 
01940                                           change = ChangeTextElementValue(element_textbox, element_textbox->GetMaskText(), resolve);
01941                                        
01942                                           if(change) 
01943                                             {                                             
01944                                               if(element_textbox->GetText()->Compare(resolve.Get(), true)) 
01945                                                 {
01946                                                   element_textbox->GetText()->Set(resolve);
01947                                                   Elements_SetToRedraw(element);    
01948                                                 }
01949                                             }
01950                                         }  
01951                                         break;
01952     }
01953 
01954 
01955   for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
01956     {
01957       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
01958       if(subelement) ChangeTextElementValue(subelement, skin);
01959     }
01960   
01961   return true;
01962  }
01963 
01964 
01976  bool UI_MANAGER::ChangeTextElementValue(UI_LAYOUT* layout)
01977 {
01978   if(!layout) return false;
01979  
01980   for(XDWORD c=0; c<layout->Elements_Get()->GetSize(); c++)
01981     {
01982       UI_ELEMENT* element = layout->Elements_Get()->Get(c);
01983       if(element) ChangeTextElementValue(element, layout->GetSkin());       
01984     }
01985 
01986   return true;
01987 }
