02001 bool UI_MANAGER::SubscribeInputEvents(bool active)
02002 {
02003   UI_XEVENT_TYPE eventtype[] = {  UI_XEVENT_TYPE_INPUT_CURSOR_MOVE            ,
02004                                   UI_XEVENT_TYPE_INPUT_CURSOR_MOTION          ,
02005                                   UI_XEVENT_TYPE_INPUT_SELECCTION             ,                                           
02006                                };
02007 
02008   for(int c=0; c<(sizeof(eventtype) / sizeof(UI_XEVENT_TYPE)); c++)
02009     {
02010       if(active)        
02011             SubscribeEvent(eventtype[c]   , this);              
02012       else  UnSubscribeEvent(eventtype[c] , this);        
02013     }
02014 
02015   return true;
02016 }
02017 
02018 
02032 bool UI_MANAGER::SubscribeOutputEvents(bool active, XOBSERVER* observer, XSUBJECT* subject)
02033 {
02034   UI_XEVENT_TYPE eventtype[] = {  UI_XEVENT_TYPE_OUTPUT_SELECTED          ,                              
02035                                   UI_XEVENT_TYPE_OUTPUT_TEXTTOCHANGE      ,
02036                                   UI_XEVENT_TYPE_OUTPUT_CHANGECONTENTS                                     
02037                                };
02038 
02039   for(int c=0; c<(sizeof(eventtype) / sizeof(UI_XEVENT_TYPE)); c++)
02040     {
02041       if(active)        
02042             observer->SubscribeEvent(eventtype[c]   , this);              
02043       else  observer->UnSubscribeEvent(eventtype[c] , this);        
02044     }
02045 
02046   return true;
02047 }
