02218 
02219 
02233 bool UI_MANAGER::GetLayoutElement_Base(XFILEXMLELEMENT* node, UI_ELEMENT* element)
02234 {
02235   double xpos   = 0.0f;
02236   double ypos   = 0.0f;    
02237   double width  = 0.0f;
02238   double height = 0.0f; 
02239   
02240   if(!element) return false;
02241     
02242   element->SetIsDetached(false);
02243 
02244   XFILEXMLELEMENT* XML_father = node->GetFather();
02245   if(XML_father)
02246     {
02247       if(!XML_father->GetName().Compare(__L("layout"), true)) element->SetIsDetached(true);        
02248     }
02249 
02250   XSTRING name;
02251   GetLayoutElementValue(node, __L("name"), name); 
02252   element->GetName()->Set(name);
02253 
02254   XSTRING position;
02255   if(GetLayoutElementValue(node, __L("xpos"), position))
02256     {
02257       if(!position.Compare(__L("left"), true))  xpos = UI_ELEMENT_TYPE_ALIGN_LEFT;
02258         else if(!position.Compare(__L("right"), true))  xpos = UI_ELEMENT_TYPE_ALIGN_RIGHT;
02259           else if(!position.Compare(__L("center"), true)) xpos = UI_ELEMENT_TYPE_ALIGN_CENTER;
02260             else GetLayoutElementValue(node, __L("xpos"), xpos); 
02261     }
02262     
02263   if(GetLayoutElementValue(node, __L("ypos"), position))
02264     {
02265       if(!position.Compare(__L("up"), true))  ypos = UI_ELEMENT_TYPE_ALIGN_UP;
02266         else if(!position.Compare(__L("down"), true))  ypos = UI_ELEMENT_TYPE_ALIGN_DOWN;
02267           else if(!position.Compare(__L("center"), true)) ypos = UI_ELEMENT_TYPE_ALIGN_CENTER;
02268             else GetLayoutElementValue(node, __L("ypos"), ypos); 
02269     } 
02270 
02271 
02272   XSTRING size;
02273   if(GetLayoutElementValue(node, __L("width"), size))
02274     {
02275       if(!size.Compare(__L("max"), true))  width = UI_ELEMENT_TYPE_ALIGN_MAX;
02276         else if(!size.Compare(__L("auto"), true))  width = UI_ELEMENT_TYPE_ALIGN_AUTO;          
02277             else GetLayoutElementValue(node, __L("width"), width); 
02278     }
02279 
02280   if(GetLayoutElementValue(node, __L("height") , size))
02281     {
02282       if(!size.Compare(__L("max"), true))  height = UI_ELEMENT_TYPE_ALIGN_MAX;
02283         else if(!size.Compare(__L("auto"), true))  height = UI_ELEMENT_TYPE_ALIGN_AUTO;          
02284             else GetLayoutElementValue(node, __L("height"), height); 
02285     }
02286    
02287   element->GetBoundaryLine()->x       = xpos;  
02288   element->GetBoundaryLine()->y       = ypos;  
02289   element->GetBoundaryLine()->width   = width;
02290   element->GetBoundaryLine()->height  = height;
02291 
02292   XSTRING directionstr;
02293   if(GetLayoutElementValue(node, __L("direction"), directionstr)) 
02294     {
02295       if(!directionstr.Compare(__L("horizotal"), true))  element->SetDirection(UI_ELEMENT_TYPE_DIRECTION_HORIZONTAL);
02296         else if(!directionstr.Compare(__L("vertical"), true))  element->SetDirection(UI_ELEMENT_TYPE_DIRECTION_VERTICAL);                  
02297     }
02298 
02299   XSTRING color;
02300   GetLayoutElementValue(node, __L("color"), color);    
02301   if(!color.IsEmpty()) element->GetColor()->SetFromString(color);
02302 
02303   XSTRING bckgrdcolor;
02304   GetLayoutElementValue(node, __L("bckgrdcolor"), bckgrdcolor);    
02305   if(!bckgrdcolor.IsEmpty()) element->GetBackgroundColor()->SetFromString(bckgrdcolor);  
02306     
02307   XSTRING visible;
02308   GetLayoutElementValue(node, __L("visible"), visible);    
02309   if(!visible.Compare(__L("no"), true))   element->SetVisible(false);
02310   if(!visible.Compare(__L("yes"), true))  element->SetVisible(true);
02311 
02312   XSTRING hasscroll;
02313   GetLayoutElementValue(node, __L("scroll"), hasscroll);
02314   if(!hasscroll.Compare(__L("no"), true))   element->SetHasScroll(false);
02315   if(!hasscroll.Compare(__L("yes"), true))  element->SetHasScroll(true);
02316 
02317 
02318   double roundrect = 0.0f;
02319   GetLayoutElementValue(node, __L("roundrect"), roundrect); 
02320   element->SetRoundRect((XDWORD)roundrect);
02321   
02322   double blinktime;
02323   if(GetLayoutElementValue(node, __L("blink"), blinktime)) element->SetBlink((XDWORD)blinktime);        
02324 
02325   XSTRING extra;
02326   GetLayoutElementValue(node, __L("extra"), extra);
02327   element->GetExtra()->Set(extra);
02328       
02329   XSTRING marginstr;
02330   if(GetLayoutElementValue(node, __L("margin"), marginstr))
02331     {
02332       int margin[UI_ELEMENT_MARGIN_MAX] = { 0, 0, 0, 0 };
02333 
02334       marginstr.UnFormat(__L("%d,%d,%d,%d") , &margin[0]
02335                                             , &margin[1]
02336                                             , &margin[2]
02337                                             , &margin[3]); 
02338 
02339       element->SetMargin(UI_ELEMENT_TYPE_ALIGN_LEFT   , (double)margin[0]); 
02340       element->SetMargin(UI_ELEMENT_TYPE_ALIGN_RIGHT  , (double)margin[1]);
02341       element->SetMargin(UI_ELEMENT_TYPE_ALIGN_UP     , (double)margin[2]); 
02342       element->SetMargin(UI_ELEMENT_TYPE_ALIGN_DOWN   , (double)margin[3]);            
02343     }
02344 
02345   return true;
02346 }
02347 
02348 
02360 bool UI_MANAGER::GetLayoutElement_CalculateBoundaryLine(UI_ELEMENT* element)
02361 {
02362   bool status = false;
02363 
02364   if(!skin_selected) return false;
02365 
02366   switch(skin_selected->GetDrawMode())
02367     {
02368       case UI_SKIN_DRAWMODE_UNKNOWN   : break;
02369 
02370       case UI_SKIN_DRAWMODE_CANVAS    : { UI_SKINCANVAS* skincanvas = (UI_SKINCANVAS*)skin_selected; 
02371                                           if(skincanvas) status = skincanvas->CalculateBoundaryLine(element);                                                                                    
02372                                         }
02373                                         break;
02374 
02375       case UI_SKIN_DRAWMODE_CONTEXT   : break;
02376     }
02377 
02378   return status;
02379 }
02380 
02381 
02394 bool UI_MANAGER::GetParentSizeFont(XFILEXMLELEMENT* node, double& sizefont)
02395 {
02396   if(!node) return false;
02397 
02398   sizefont = 0;
02399     
02400   if(!GetLayoutElementValue(node, __L("sizefont"), sizefont))
02401     {
02402       return GetParentSizeFont(node->GetFather(), sizefont);
02403     }
02404 
02405   return true;
02406 }
02407 
02408 
02422 UI_ELEMENT* UI_MANAGER::GetLayoutElement_Text(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
02423 {
02424   XSTRING           text;
02425   double            sizefont      = 0;
02426   bool              havemask      = false;
02427   UI_ELEMENT_TEXT*  element_text  = NULL;
02428 
02429   if(!skin_selected) return NULL;
02430 
02431   if(element_legacy)
02432     {
02433       element_text = (UI_ELEMENT_TEXT*)element_legacy;
02434     }
02435    else
02436     {
02437       element_text = new UI_ELEMENT_TEXT();
02438       if(!element_text) return NULL;
02439     }
02440 
02441   element_text->SetFather(father);
02442 
02443 
02444   if(!GetLayoutElement_Base(node, element_text))
02445     {
02446       delete element_text;
02447       return NULL;
02448     }
02449 
02450   SetLevelAuto(element_text, father);
02451 
02452   if(!GetLayoutElementValue(node, __L("sizefont"), sizefont))
02453     {
02454       if(!GetParentSizeFont(node->GetFather(), sizefont))
02455         {
02456           delete element_text;
02457           return NULL;
02458         }
02459     }
02460 
02461   element_text->SetSizeFont((XDWORD)sizefont);
02462 
02463   text = node->GetValue().Get();
02464 
02465   XSTRING* textcache = GEN_UI_TEXTS.Get(text);
02466   if(textcache)  text = textcache->Get();
02467 
02468   if(text.Find(UI_MANAGER_LAYOUT_TEXT_INI, true) != XSTRING_NOTFOUND) 
02469     {
02470       if(text.Find(UI_MANAGER_LAYOUT_TEXT_END, true) != XSTRING_NOTFOUND)  havemask = true;
02471     }
02472   
02473   if(havemask)
02474     {
02475       element_text->GetMaskText()->Set(text.Get());
02476       element_text->GetText()->Empty();
02477       
02478     } 
02479    else
02480     { 
02481       element_text->GetMaskText()->Empty();
02482       element_text->GetText()->Set(text.Get());
02483     }
02484 
02485   double maxsizetext = 0.0f;  
02486   if(GetLayoutElementValue(node, __L("maxsizetext"), maxsizetext))
02487     {               
02488       element_text->SetMaxSizeText((XDWORD)maxsizetext);    
02489     }
02490 
02491   if(!element_text->GetMaskText()->IsEmpty()) 
02492     {
02493       XSTRING  maskvalue;
02494       XSTRING  maskresolved;
02495       XSTRING  _text;
02496       int      start      = 0;
02497       int      new_start  = XSTRING_NOTFOUND;
02498       bool     change     = false;
02499   
02500       do{ int new_start = GEN_USERINTERFACE.GetOutputTextChangeID(element_text->GetMaskText(), start, maskvalue);
02501           if(new_start != XSTRING_NOTFOUND)
02502             {                                    
02503               if(maskvalue.Find(UI_MANAGER_LAYOUT_TEXT_TRANSLATE, true) != XSTRING_NOTFOUND)
02504                 {
02505                   XCHAR*    translate_str  = NULL;
02506                   XSTRING   maskID;
02507                   int       ID             = 0;
02508 
02509                   maskID  = UI_MANAGER_LAYOUT_TEXT_TRANSLATE;
02510                   maskID += __L("%d");
02511 
02512                   maskvalue.UnFormat(maskID.Get(), &ID);
02513 
02514                   translate_str = GEN_XTRANSLATION.Translate_GetSentence(ID);
02515                   if(translate_str)  maskresolved.Set(translate_str);
02516                 } 
02517   
02518               if(!maskresolved.IsEmpty()) 
02519                 {
02520                   element_text->GetMaskText()->DeleteCharacters(new_start, maskvalue.GetSize() +3 ); 
02521                   element_text->GetMaskText()->Insert(maskresolved, new_start);  
02522                   change = true;                
02523                 }
02524             }
02525 
02526         } while(new_start != XSTRING_NOTFOUND);    
02527 
02528 
02529       if(change)
02530         {
02531           havemask = false;
02532 
02533           text = element_text->GetMaskText()->Get();
02534           if(text.Find(UI_MANAGER_LAYOUT_TEXT_INI, true) != XSTRING_NOTFOUND) 
02535             {
02536               if(text.Find(UI_MANAGER_LAYOUT_TEXT_END, true) != XSTRING_NOTFOUND)  havemask = true;
02537             }
02538   
02539           if(havemask)
02540             {
02541               element_text->GetMaskText()->Set(text.Get());
02542               element_text->GetText()->Empty();
02543       
02544             } 
02545            else
02546             { 
02547               element_text->GetMaskText()->Empty();
02548               element_text->GetText()->Set(text.Get());
02549             } 
02550          }
02551   
02552     }
02553   
02554   GetLayoutElement_CalculateBoundaryLine(element_text);
02555    
02556   return element_text;
02557 }
02558 
02559 
02573 UI_ELEMENT* UI_MANAGER::GetLayoutElement_TextBox(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
02574 {  
02575   UI_ELEMENT_TEXTBOX* element_textbox   = NULL;
02576   XSTRING             text;
02577 
02578   if(!skin_selected) return NULL;
02579 
02580   if(element_legacy)
02581     {
02582       element_textbox = (UI_ELEMENT_TEXTBOX*)element_legacy;
02583     }
02584    else
02585     {
02586       element_textbox = new UI_ELEMENT_TEXTBOX();
02587       if(!element_textbox) return NULL;
02588     }
02589 
02590   element_textbox->SetFather(father);
02591 
02592   if(!GetLayoutElement_Base(node, element_textbox))
02593     {
02594       delete element_textbox;
02595       return NULL;
02596     }
02597 
02598   SetLevelAuto(element_textbox, father);
02599 
02600   if(!element_textbox->GetBoundaryLine()->width)  return NULL;
02601   if(!element_textbox->GetBoundaryLine()->height) return NULL;
02602 
02603   double sizefont = 0;  
02604   if(!GetLayoutElementValue(node, __L("sizefont"), sizefont))
02605     {
02606       if(!GetParentSizeFont(node->GetFather(), sizefont))
02607         {
02608           delete element_textbox;
02609           return NULL;
02610         }
02611     }
02612 
02613   element_textbox->SetSizeFont((XDWORD)sizefont);
02614 
02615   double linespacing = UI_ELEMENT_TEXTBOX_DEFAULTLINESPACING;
02616   GetLayoutElementValue(node, __L("linespacing"), linespacing);     
02617   element_textbox->SetLineSpacing((XDWORD)linespacing);
02618 
02619   XSTRING                textalignmentstr;
02620   UI_ELEMENT_TYPE_ALIGN  textalignment;
02621   if(GetLayoutElementValue(node, __L("textalignment"), textalignmentstr))
02622     {
02623       if(!textalignmentstr.Compare(__L("left"), true))  textalignment = UI_ELEMENT_TYPE_ALIGN_LEFT;
02624         else if(!textalignmentstr.Compare(__L("right"), true))  textalignment = UI_ELEMENT_TYPE_ALIGN_RIGHT;
02625           else if(!textalignmentstr.Compare(__L("center"), true)) textalignment = UI_ELEMENT_TYPE_ALIGN_CENTER;
02626     }
02627   element_textbox->SetTextAlignment(textalignment);
02628 
02629 
02630   XSTRING wordclippingstr;  
02631   element_textbox->SetIsWordClipping(false);
02632   if(GetLayoutElementValue(node, __L("wordclipping"), wordclippingstr))
02633     {
02634       if(!wordclippingstr.Compare(__L("no"), true))   element_textbox->SetIsWordClipping(false);
02635       if(!wordclippingstr.Compare(__L("yes"), true))  element_textbox->SetIsWordClipping(true);
02636     }
02637 
02638   text.Empty();
02639 
02640   for(int c=0; c<node->GetNElements(); c++)
02641     {
02642       XFILEXMLELEMENT* subnode = node->GetElement(c);
02643       if(subnode)
02644         {
02645           if(!subnode->GetName().Compare(__L("text"), true))
02646             {                                                                  
02647               text.Add(subnode->GetValue().Get());
02648               text.Add(__L("\n"));
02649             }    
02650         }
02651     }
02652   
02653   if(!text.IsEmpty()) 
02654     {
02655       XSTRING  value;
02656       XSTRING  resolved;      
02657       int      start      = 0;
02658       int      new_start  = XSTRING_NOTFOUND;
02659       bool     change     = false;
02660   
02661       do{ int new_start = GEN_USERINTERFACE.GetOutputTextChangeID(&text, start, value);
02662           if(new_start != XSTRING_NOTFOUND)
02663             {                                    
02664               if(value.Find(UI_MANAGER_LAYOUT_TEXT_TRANSLATE, true) != XSTRING_NOTFOUND)
02665                 {
02666                   XCHAR*    translate_str  = NULL;
02667                   XSTRING   IDstr;
02668                   int       ID             = 0;
02669 
02670                   IDstr  = UI_MANAGER_LAYOUT_TEXT_TRANSLATE;
02671                   IDstr += __L("%d");
02672 
02673                   value.UnFormat(IDstr.Get(), &ID);
02674 
02675                   translate_str = GEN_XTRANSLATION.Translate_GetSentence(ID);
02676                   if(translate_str) resolved.Set(translate_str);
02677                 } 
02678   
02679               if(!resolved.IsEmpty()) 
02680                 {
02681                   text.DeleteCharacters(new_start, value.GetSize() +3 ); 
02682                   text.Insert(resolved, new_start);  
02683                   change = true;                
02684                 }
02685             }
02686 
02687         } while(new_start != XSTRING_NOTFOUND);      
02688     }
02689 
02690   element_textbox->GetMaskText()->Set(text);
02691 
02692   GetLayoutElement_CalculateBoundaryLine(element_textbox);
02693 
02694   return element_textbox;
02695 }
02696 
