02711 UI_ELEMENT* UI_MANAGER::GetLayoutElement_Image(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
02712 {
02713   XPATH             xpathimg;
02714   XSTRING           namefileimg;
02715   UI_ELEMENT_IMAGE* element_image = NULL;
02716 
02717   if(!skin_selected) return NULL;
02718 
02719   if(element_legacy)
02720     {
02721       element_image = (UI_ELEMENT_IMAGE*)element_legacy;
02722     }
02723    else
02724     {
02725       element_image = new UI_ELEMENT_IMAGE();
02726       if(!element_image) return NULL;
02727     }
02728 
02729   element_image->SetFather(father);
02730   
02731   if(!GetLayoutElement_Base(node, element_image))
02732     {
02733       delete element_image;
02734       return NULL;
02735     }
02736 
02737   SetLevelAuto(element_image, father);
02738 
02739   double alpha = 0;
02740   if(GetLayoutElementValue(node, __L("alpha"), alpha))
02741     {
02742       element_image->SetAlpha((XBYTE)alpha);
02743     }
02744 
02745   namefileimg = node->GetValue().Get();
02746 
02747  
02748   if(!namefileimg.IsEmpty()) 
02749     {
02750       UI_ANIMATION* animation = GetOrAddAnimationCache(__L(""), namefileimg.Get());
02751       if(animation) 
02752         { 
02753           GRPBITMAP* bitmap = NULL; 
02754           bitmap = animation->GetBitmap(); 
02755           if(bitmap) element_image->SetImage(bitmap);             
02756         }
02757     }
02758      
02759   GetLayoutElement_CalculateBoundaryLine(element_image);
02760   
02761   return element_image;
02762 }
