02764 
02778 UI_ELEMENT* UI_MANAGER::GetLayoutElement_Animation(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
02779 {
02780   XSTRING               text;
02781   XSTRING               namefileimg;
02782   XPATH                 xpathimg;
02783   int                   sizefont          = 0;
02784   UI_ELEMENT_ANIMATION* element_animation = NULL;
02785 
02786   if(!skin_selected) return NULL;
02787 
02788   if(element_legacy)
02789     {
02790       element_animation = (UI_ELEMENT_ANIMATION*)element_legacy;
02791     }
02792    else
02793     {
02794       element_animation = new UI_ELEMENT_ANIMATION();
02795       if(!element_animation) return NULL;
02796     }
02797 
02798   element_animation->SetFather(father);
02799   
02800   if(!GetLayoutElement_Base(node, element_animation))
02801     {
02802       delete element_animation;
02803       return NULL;
02804     } 
02805 
02806   SetLevelAuto(element_animation, father);
02807 
02808   XSTRING statestring;  
02809   if(GetLayoutElementValue(node, __L("state"), statestring))
02810     {
02811       if(!statestring.Compare(__L("play"), true))                                                                     
02812         {
02813           element_animation->Play();
02814         }
02815         else
02816         {
02817           if(!statestring.Compare(__L("stop"), true))                                                                     
02818             {
02819               element_animation->Stop();
02820             }   
02821             else
02822             {       
02823               if(!statestring.Compare(__L("pause"), true))                                                                     
02824                 {
02825                   element_animation->Pause();    
02826                 }
02827             }
02828         }
02829     }
02830 
02831   double timedelay = 0;
02832   if(GetLayoutElementValue(node, __L("timedelay"), timedelay))
02833     {
02834       element_animation->SetMilliSecondsDelay((XDWORD)timedelay);
02835     }
02836 
02837   double alpha = 0;
02838   if(GetLayoutElementValue(node, __L("alpha"), alpha))
02839     {
02840       element_animation->SetAlpha((XBYTE)alpha);
02841     }
02842 
02843   for(int c=0; c<node->GetNElements(); c++)
02844     {
02845       XFILEXMLELEMENT* nodeelement =  node->GetElement(c);
02846       if(nodeelement)
02847         {  
02848           if(!nodeelement->GetName().Compare(__L("image")))
02849             {              
02850               namefileimg = nodeelement->GetValue();       
02851 
02852               UI_ANIMATION* animation = GetOrAddAnimationCache(__L(""), namefileimg.Get());
02853               if(animation)
02854                 {
02855                   for(XDWORD d=0; d<animation->GetBitmaps()->GetSize(); d++)
02856                     {
02857                       UI_ELEMENT_IMAGE* element_img = new UI_ELEMENT_IMAGE();
02858                       if(element_img) 
02859                         {
02860                           GRPBITMAP* bitmap = NULL; 
02861 
02862                           element_img->SetFather(element_animation);       
02863 
02864                           GetLayoutElement_Base(nodeelement, element_img); 
02865 
02866                           SetLevelAuto(element_img, element_animation);
02867 
02868                           bitmap = animation->GetBitmaps()->Get(d);
02869                           if(bitmap) 
02870                             { 
02871                               element_img->SetImage(bitmap);
02872 
02873                               switch(skin_selected->GetDrawMode())
02874                                 {
02875                                   case UI_SKIN_DRAWMODE_UNKNOWN   : break;
02876 
02877                                   case UI_SKIN_DRAWMODE_CANVAS    : { UI_SKINCANVAS* skincanvas = (UI_SKINCANVAS*)skin_selected; 
02878                                                                       if(skincanvas) skincanvas->CalculateBoundaryLine(element_img); 
02879                                                                     }
02880                                                                     break;
02881    
02882                                   case UI_SKIN_DRAWMODE_CONTEXT   : break;
02883 
02884                                 }
02885 
02886                               element_img->SetAlpha(element_animation->GetAlpha());
02887                                                                   
02888                               element_animation->GetComposeElements()->Add(element_img);                                                                                                
02889                             } 
02890                         }
02891                     }      
02892                 }
02893             }
02894         }
02895     }
02896 
02897   GetLayoutElement_CalculateBoundaryLine(element_animation);
02898 
02899   return element_animation;
02900 }
02901 
02902 
02916 UI_ELEMENT* UI_MANAGER::GetLayoutElement_Option(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
02917 {
02918   XSTRING             text;
02919   XSTRING             namefileimg;
02920   XPATH               xpathimg;
02921   double              sizefont  = 0;
02922   UI_ELEMENT_OPTION*  element_option = NULL;
02923 
02924   if(!skin_selected) return NULL;
02925 
02926   if(element_legacy)
02927     {
02928       element_option =  (UI_ELEMENT_OPTION*)element_legacy;
02929     }
02930    else
02931     {
02932       element_option = new UI_ELEMENT_OPTION();
02933       if(!element_option) return NULL;
02934     }
02935 
02936   element_option->SetFather(father);
02937 
02938   if(!GetLayoutElement_Base(node, element_option))
02939     {
02940       delete element_option;
02941       return NULL;
02942     } 
02943 
02944   SetLevelAuto(element_option, father);
02945 
02946   element_option->SetActive(true);
02947 
02948   GetLayoutElementValue(node, __L("sizefont"), sizefont);
02949 
02950  
02951   XSTRING selectablestr;
02952   GetLayoutElementValue(node, __L("selectablestatus"), selectablestr);
02953   element_option->SetSelectableStateFromString(selectablestr);
02954     
02955   XSTRING allocationtextstr;
02956   if(GetLayoutElementValue(node, __L("allocationtext"), allocationtextstr))
02957     {
02958       if(!allocationtextstr.Compare(__L("none")     , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE);  
02959       if(!allocationtextstr.Compare(__L("up")       , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP);
02960       if(!allocationtextstr.Compare(__L("down")     , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN);  
02961       if(!allocationtextstr.Compare(__L("right")    , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT);  
02962         if(!allocationtextstr.Compare(__L("left")     , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT); 
02963       if(!allocationtextstr.Compare(__L("center")   , true)) element_option->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER); 
02964     }
02965 
02966   for(int c=0; c<node->GetNElements(); c++)
02967     {
02968       XFILEXMLELEMENT* nodeelement =  node->GetElement(c);
02969       if(nodeelement)
02970         {
02971           XSTRING type;
02972           if(GetLayoutElementValue(nodeelement, __L("type"), type))
02973             {        
02974               UI_ELEMENT* element = CreatePartialLayout(nodeelement, element_option);
02975               if(element) 
02976                 {                   
02977                   switch(element->GetType())
02978                     {
02979                       case UI_ELEMENT_TYPE_ANIMATION  : element_option->Set_UIAnimation((UI_ELEMENT_ANIMATION*)element);                    
02980                                                         element_option->GetComposeElements()->Add(element);
02981                                                         break;
02982 
02983                       case UI_ELEMENT_TYPE_TEXT       : element_option->Set_UIText((UI_ELEMENT_TEXT*)element);                    
02984                                                         element_option->GetComposeElements()->Add(element);
02985                                                         break;                     
02986                     }                     
02987                 }              
02988             }
02989         }  
02990     }
02991   
02992   GetLayoutElement_CalculateBoundaryLine(element_option);
02993 
02994   return element_option;
02995 }
02996 
02997 
03011 UI_ELEMENT* UI_MANAGER::GetLayoutElement_MultiOption(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
03012 {
03013   UI_ELEMENT_MULTIOPTION* element_multioption = NULL;
03014   double                  sizefont            = 0;
03015 
03016   if(!skin_selected) return NULL;
03017 
03018   if(element_legacy)
03019     {
03020       element_multioption =  (UI_ELEMENT_MULTIOPTION*)element_legacy;
03021     }
03022    else
03023     {
03024       element_multioption = new UI_ELEMENT_MULTIOPTION();
03025       if(!element_multioption) return NULL;     
03026     }
03027 
03028   element_multioption->SetFather(father);
03029  
03030   if(!GetLayoutElement_Base(node, element_multioption))
03031     {
03032       delete element_multioption;
03033       return NULL;
03034     }
03035 
03036   SetLevelAuto(element_multioption, father);
03037     
03038   for(int c=0; c<node->GetNElements(); c++)
03039     {
03040       XFILEXMLELEMENT* nodeelement =  node->GetElement(c);
03041       if(nodeelement)
03042         {
03043           UI_ELEMENT* element = CreatePartialLayout(nodeelement, element_multioption);
03044           if(element) 
03045             {  
03046               element->SetFather(element_multioption);                
03047               element_multioption->GetComposeElements()->Add(element);                        
03048             }       
03049         }  
03050     }
03051   
03052   GetLayoutElement_CalculateBoundaryLine(element_multioption);
03053 
03054   for(XDWORD c=0; c<element_multioption->GetComposeElements()->GetSize(); c++)
03055     {
03056       UI_ELEMENT* element = element_multioption->GetComposeElements()->Get(c);
03057       if(element) GetLayoutElement_CalculateBoundaryLine(element);
03058     }
03059  
03060   return element_multioption;  
03061 }
