03135 
03149 UI_ELEMENT* UI_MANAGER::GetLayoutElement_Form(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
03150 {
03151   UI_ELEMENT_FORM* element_form  = NULL;
03152   double           sizefont      = 0;
03153 
03154   if(!skin_selected) return NULL;
03155 
03156   if(element_legacy)
03157     {
03158       element_form =  (UI_ELEMENT_FORM*)element_legacy;
03159     }
03160    else
03161     {
03162       element_form = new UI_ELEMENT_FORM();
03163       if(!element_form) return NULL;      
03164     }
03165 
03166   element_form->SetFather(father);
03167  
03168   if(!GetLayoutElement_Base(node, element_form))
03169     {
03170       delete element_form;
03171       return NULL;
03172     }
03173 
03174   SetLevelAuto(element_form, father);
03175 
03176   XSTRING linecolor;
03177   GetLayoutElementValue(node, __L("linecolor"), linecolor);    
03178   if(!linecolor.IsEmpty()) element_form->GetLineColor()->SetFromString(linecolor);  
03179     
03180   GetLayoutElement_CalculateBoundaryLine(element_form);
03181 
03182   for(int c=0; c<node->GetNElements(); c++)
03183     {
03184       XFILEXMLELEMENT* nodeelement =  node->GetElement(c);
03185       if(nodeelement)
03186         {
03187           UI_ELEMENT* element = CreatePartialLayout(nodeelement, element_form);
03188           if(element) 
03189             {  
03190               element->SetFather(element_form);                
03191               element_form->GetComposeElements()->Add(element);                        
03192             }       
03193         }  
03194     }
03195 
03196   GetLayoutElement_CalculateBoundaryLine(element_form);
03197 
03198   for(XDWORD c=0; c<element_form->GetComposeElements()->GetSize(); c++)
03199     {
03200       UI_ELEMENT* subelement = element_form->GetComposeElements()->Get(c);
03201       if(subelement) GetLayoutElement_CalculateBoundaryLine(subelement);
03202     }
03203 
03204   XSTRING visibleformstr;
03205   if(GetLayoutElementValue(node, __L("visiblerect"), visibleformstr))
03206     {
03207       if(!visibleformstr.IsEmpty())
03208         {                     
03209           int value[4] = { 0, 0, 0, 0 }; 
03210 
03211           visibleformstr.UnFormat(__L("%d,%d,%d,%d")  , &value[0], &value[1], &value[2], &value[3]); 
03212 
03213           element_form->GetVisibleRect()->x = element_form->GetXPosition() + value[0];
03214           element_form->GetVisibleRect()->y = element_form->GetYPosition() + value[1];      
03215 
03216           if(value[2]) 
03217             {       
03218               element_form->GetVisibleRect()->width = value[2]; 
03219             }
03220            else
03221             {
03222               element_form->GetVisibleRect()->width = element_form->GetBoundaryLine()->width; 
03223             }
03224 
03225           if(value[3])
03226             {
03227               element_form->GetVisibleRect()->height  = value[3];
03228             }
03229            else
03230             {
03231               element_form->GetVisibleRect()->height  = element_form->GetBoundaryLine()->height;
03232             }
03233 
03234           element_form->GetBoundaryLine()->SetAround((*element_form->GetVisibleRect()));
03235         }
03236     }
03237    else 
03238     {
03239       element_form->GetVisibleRect()->x       = element_form->GetXPosition();
03240       element_form->GetVisibleRect()->y       = element_form->GetYPosition();
03241       element_form->GetVisibleRect()->width   = element_form->GetBoundaryLine()->width;
03242       element_form->GetVisibleRect()->height  = element_form->GetBoundaryLine()->height;      
03243     }
03244    
03245   return element_form;  
03246 }
