03321 
03335 UI_ELEMENT* UI_MANAGER::GetLayoutElement_ProgressBar(XFILEXMLELEMENT* node, UI_ELEMENT* father, UI_ELEMENT* element_legacy)
03336 {
03337   if(!skin_selected) return NULL;
03338 
03339   XSTRING                   text;
03340   XSTRING                   namefileimg;
03341   XPATH                     xpathimg;
03342   double                    sizefont  = 0;
03343   UI_ELEMENT_PROGRESSBAR*   element_progressbar = NULL;
03344 
03345   if(!skin_selected) return NULL;
03346 
03347   if(element_legacy)
03348     {
03349       element_progressbar =  (UI_ELEMENT_PROGRESSBAR*)element_legacy;
03350     }
03351    else
03352     {
03353       element_progressbar = new UI_ELEMENT_PROGRESSBAR();
03354       if(!element_progressbar) return NULL;
03355     }
03356 
03357   element_progressbar->SetFather(father);
03358 
03359   if(!GetLayoutElement_Base(node, element_progressbar))
03360     {
03361       delete element_progressbar;
03362       return NULL;
03363     } 
03364 
03365   SetLevelAuto(element_progressbar, father);
03366 
03367   element_progressbar->SetActive(true);
03368 
03369   GetLayoutElementValue(node, __L("sizefont"), sizefont);
03370 
03371     
03372   XSTRING allocationtextstr;
03373   if(GetLayoutElementValue(node, __L("allocationtext"), allocationtextstr))
03374     {
03375       if(!allocationtextstr.Compare(__L("none")     , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE);  
03376       if(!allocationtextstr.Compare(__L("up")       , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP);
03377       if(!allocationtextstr.Compare(__L("down")     , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN);  
03378       if(!allocationtextstr.Compare(__L("right")    , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT);  
03379         if(!allocationtextstr.Compare(__L("left")     , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT); 
03380       if(!allocationtextstr.Compare(__L("center")   , true)) element_progressbar->SetAllocationTextType(UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER); 
03381     }  
03382 
03383 
03384   XSTRING linecolor;
03385   GetLayoutElementValue(node, __L("linecolor"), linecolor);    
03386   if(!linecolor.IsEmpty()) element_progressbar->GetLineColor()->SetFromString(linecolor);  
03387 
03388   for(int c=0; c<node->GetNElements(); c++)
03389     {
03390       XFILEXMLELEMENT* nodeelement =  node->GetElement(c);
03391       if(nodeelement)
03392         {
03393           XSTRING type;
03394           if(GetLayoutElementValue(nodeelement, __L("type"), type))
03395             {        
03396               UI_ELEMENT* element = CreatePartialLayout(nodeelement, element_progressbar);
03397               if(element) 
03398                 {            
03399                   element->SetFather(element_progressbar);  
03400        
03401                   switch(element->GetType())
03402                     {
03403                       case UI_ELEMENT_TYPE_ANIMATION  : element_progressbar->Set_UIAnimation((UI_ELEMENT_ANIMATION*)element);                    
03404                                                         element_progressbar->GetComposeElements()->Add(element);
03405                                                         break;
03406 
03407                       case UI_ELEMENT_TYPE_TEXT       : element_progressbar->Set_UIText((UI_ELEMENT_TEXT*)element);                    
03408                                                         element_progressbar->GetComposeElements()->Add(element);
03409                                                         break;                     
03410                     }                     
03411                 }              
03412             }
03413         }  
03414     }
03415 
03416   XSTRING continuouscyclestr;
03417   if(GetLayoutElementValue(node, __L("continuouscycle"), continuouscyclestr))
03418     {
03419       if(!continuouscyclestr.IsEmpty())
03420         {
03421           int value[3] = { 0, 0, 0 }; 
03422 
03423           continuouscyclestr.UnFormat(__L("%d,%d,%d")  , &value[0], &value[1], &value[2]); 
03424 
03425           if(!value[0]) value[0] = UI_ELEMENT_PROGRESSBAR_CCYCLE_DEFAULTSIZESEGMENT;
03426           if(!value[1]) value[1] = UI_ELEMENT_PROGRESSBAR_CCYCLE_DEFAULTSTEPSEGMENT;
03427           if(!value[1]) value[1] = UI_ELEMENT_PROGRESSBAR_CCYCLE_DEFAULTTIMESTEP;
03428 
03429           element_progressbar->ContinuousCycle_Set(true,  value[0], value[1], value[2]);             
03430         }
03431     }
03432 
03433   XSTRING progressrectstr;
03434   if(GetLayoutElementValue(node, __L("progressrect"), progressrectstr))
03435     {
03436       if(!progressrectstr.IsEmpty())
03437         {
03438           UI_ELEMENT* element_progressrect = NULL;
03439 
03440           element_progressrect = new UI_ELEMENT();
03441           if(element_progressrect)
03442             {
03443               int value[4] = { 0, 0, 0, 0 }; 
03444 
03445               progressrectstr.UnFormat(__L("%d,%d,%d,%d")  , &value[0], &value[1], &value[2], &value[3]); 
03446 
03447               element_progressrect->GetBoundaryLine()->x = element_progressbar->GetXPosition() + value[0];
03448               element_progressrect->GetBoundaryLine()->y = element_progressbar->GetYPosition() + value[1];      
03449 
03450               if(value[2]) 
03451                 {       
03452                   element_progressrect->GetBoundaryLine()->width = value[2]; 
03453                 }
03454                else
03455                 {
03456                   element_progressrect->GetBoundaryLine()->width = element_progressbar->GetBoundaryLine()->width; 
03457                 }
03458 
03459               if(value[3])
03460                 {
03461                   element_progressrect->GetBoundaryLine()->height  = value[3];
03462                 }
03463                else
03464                 {
03465                   element_progressrect->GetBoundaryLine()->height  = element_progressbar->GetBoundaryLine()->height;
03466                 }
03467 
03468               //element_progressbar->GetBoundaryLine()->SetAround((*element_progressrect->GetBoundaryLine()));
03469               element_progressbar->SetProgressRect(element_progressrect);
03470               element_progressrect->SetFather(element_progressbar);
03471 
03472               element_progressbar->GetComposeElements()->Add(element_progressrect);
03473             }
03474         }
03475     }
03476    else 
03477     {
03478       element_progressbar->SetProgressRect(NULL);
03479     }
03480 
03481 
03482   GetLayoutElement_CalculateBoundaryLine(element_progressbar);
03483 
03484   return element_progressbar;
03485 }
03486 
03487 
03500 UI_ELEMENT* UI_MANAGER::CreatePartialLayout(XFILEXMLELEMENT* nodeelement, UI_ELEMENT* father)
03501 {
03502   XSTRING       value;
03503   UI_ELEMENT*   element  = NULL;
03504 
03505   if(!skin_selected) return NULL;
03506  
03507   if(GetLayoutElementValue(nodeelement, __L("type"), value))
03508     {                              
03509       if(!value.Compare(__L("text")           , true))  
03510         {
03511           element = GetLayoutElement_Text(nodeelement, father);          
03512         }
03513 
03514       if(!value.Compare(__L("textbox")        , true))  
03515         {
03516           element = GetLayoutElement_TextBox(nodeelement, father);          
03517         }
03518 
03519       if(!value.Compare(__L("image")          , true))  
03520         {
03521           element = GetLayoutElement_Image(nodeelement, father);
03522         }
03523 
03524       if(!value.Compare(__L("animation")      , true))  
03525         {
03526           element = GetLayoutElement_Animation(nodeelement, father);          
03527         }
03528 
03529       if(!value.Compare(__L("option")         , true))  
03530         {
03531           element = GetLayoutElement_Option(nodeelement, father);          
03532         }
03533 
03534       if(!value.Compare(__L("multioption")    , true))  
03535         {
03536           element = GetLayoutElement_MultiOption(nodeelement, father);          
03537         }
03538 
03539       if(!value.Compare(__L("button")         , true))  
03540         { 
03541           element = GetLayoutElement_Button(nodeelement, father); 
03542         }
03543 
03544       if(!value.Compare(__L("checkbox")       , true))  
03545         {
03546           element = GetLayoutElement_CheckBox(nodeelement, father);          
03547         }
03548 
03549       if(!value.Compare(__L("edittext")       , true))  
03550         {
03551           element = GetLayoutElement_EditText(nodeelement, father);          
03552         }
03553 
03554       if(!value.Compare(__L("form")           , true))  
03555         {
03556           element = GetLayoutElement_Form(nodeelement, father);
03557         }
03558 
03559       if(!value.Compare(__L("menu")           , true))  
03560         {
03561           element = GetLayoutElement_Menu(nodeelement, father);      
03562         }
03563 
03564       if(!value.Compare(__L("listbox")        , true))  
03565         {
03566           element = GetLayoutElement_ListBox(nodeelement, father);      
03567         }
03568 
03569       if(!value.Compare(__L("progressbar")    , true))  
03570         {
03571           element = GetLayoutElement_ProgressBar(nodeelement, father);      
03572         }
03573     }
03574 
03575   return element;
03576 }
03577 
03578 
03590 bool UI_MANAGER::CreateLayouts(XFILEXML& xml)
03591 { 
03592   if(!xml.ReadAndDecodeAllLines()) return false;
03593 
03594   XFILEXMLELEMENT*  root          = xml.GetRoot();  
03595   XSTRING           nameskin;  
03596   UI_SKIN_DRAWMODE  drawmode      = UI_SKIN_DRAWMODE_UNKNOWN;  
03597   XSTRING           raster_fontname;
03598   XSTRING           vector_fontname; 
03599   XSTRING           background_color; 
03600   XSTRING           background_namefile; 
03601   
03602   if(!root) return false;
03603     
03604   for(int c=0; c<root->GetNElements(); c++)
03605     {
03606       XFILEXMLELEMENT* nodeskin = root->GetElement(c);
03607       if(nodeskin)
03608         {
03609           if(!nodeskin->GetName().Compare(__L("skin"), true))
03610             {                 
03611               XCHAR*    value;                             
03612               XSTRING   drawmodestr;
03613                  
03614               value = nodeskin->GetValueAttribute(__L("name"));
03615               if(value) nameskin = value;
03616 
03617               drawmodestr = nodeskin->GetValueAttribute(__L("drawmode"));  
03618               if(!drawmodestr.Compare(__L("canvas"), true))   drawmode = UI_SKIN_DRAWMODE_CANVAS;
03619               if(!drawmodestr.Compare(__L("context"), true))  drawmode = UI_SKIN_DRAWMODE_CONTEXT;                  
03620 
03621               value = nodeskin->GetValueAttribute(__L("rasterfont"));                          
03622               if(value) raster_fontname = value;
03623                                           
03624               value = nodeskin->GetValueAttribute(__L("vectorfont"));                          
03625               if(value) vector_fontname = value;    
03626 
03627               value = nodeskin->GetValueAttribute(__L("backgroundcolor"));                          
03628               if(value) background_color = value;                                       
03629 
03630               value = nodeskin->GetValueAttribute(__L("backgroundimg"));                          
03631               if(value) background_namefile = value;                                       
03632             }                  
03633         }
03634     }
03635 
03636   skin_selected = Skin_Get(nameskin, drawmode);
03637   if(!skin_selected) return false;
03638       
03639   skin_selected->GetRasterFont()->Set(raster_fontname);
03640   skin_selected->GetVectorFont()->Set(vector_fontname);
03641 
03642   if(!skin_selected->LoadFonts()) return false;
03643 
03644   skin_selected->Background_GetColor()->Set(background_color);
03645   skin_selected->Background_GetNameFile()->Set(background_namefile);
03646  
03647   if(!background_namefile.IsEmpty())
03648     {
03649       if(!skin_selected->Background_LoadBitmap()) return false;
03650     }
03651 
03652   for(int c=0; c<root->GetNElements(); c++)
03653     {
03654       XFILEXMLELEMENT*  nodecacheelement = root->GetElement(c);
03655       if(nodecacheelement)
03656         {
03657           CreateCacheElements(nodecacheelement);
03658         }
03659     }
03660 
03661   for(int c=0; c<root->GetNElements(); c++)
03662     {
03663       XFILEXMLELEMENT* nodelayout = root->GetElement(c);
03664       if(nodelayout)
03665         {
03666           if(!nodelayout->GetName().Compare(__L("layout"), true))
03667             {
03668               XSTRING   namelayout;  
03669               XSTRING   background_color;
03670               XSTRING   background_namefile;
03671               XCHAR*    value;                             
03672 
03673               value = nodelayout->GetValueAttribute(__L("name"));
03674               if(value) namelayout = value;         
03675               if(!namelayout.IsEmpty())
03676                 {                      
03677                   value = nodelayout->GetValueAttribute(__L("backgroundcolor"));                          
03678                   if(value) background_color = value;                                       
03679 
03680                   value = nodelayout->GetValueAttribute(__L("backgroundimg"));                          
03681                   if(value) background_namefile = value;                  
03682 
03683                   UI_LAYOUT* layout = new UI_LAYOUT(skin_selected);
03684                   if(layout)
03685                     {
03686                       layout->GetNameID()->Set(namelayout); 
03687                       layout->GetBackgroundColor()->SetFromString(background_color);
03688                       layout->GetBackgroundFilename()->Set(background_namefile);
03689 
03690                       if(!layout->GetNameID()->Compare(UI_MANAGER_LAYOUT_INALL_NAME, true))
03691                         {
03692                           inall_layout = layouts.GetSize();
03693                         }
03694 
03695                       Layouts_Add(layout); 
03696 
03697                       for(int d=0 ; d<nodelayout->GetNElements(); d++)
03698                         {
03699                           XFILEXMLELEMENT* nodeelement =  nodelayout->GetElement(d);
03700                           if(nodeelement)
03701                             {
03702                               UI_ELEMENT* element = CreatePartialLayout(nodeelement, NULL);
03703                               if(element) layout->Elements_Add(element);                                                                                                                              
03704                             }                              
03705                         } 
03706                     }  
03707                 }
03708             }                  
03709         }
03710     }
03711     
03712   return true;
03713 }
03714 
03715 
03728 bool UI_MANAGER::CreateCacheElements(XFILEXMLELEMENT* nodeelement, bool recursive)
03729 {
03730   XSTRING name;          
03731   XCHAR*  value;  
03732   bool    status = false;
03733 
03734   if(!nodeelement->GetName().Compare(__L("color"), true))
03735     {                                                                  
03736       value = nodeelement->GetValueAttribute(__L("name"));
03737       if(value) 
03738         {
03739           name = value;
03740         }
03741               
03742       value = nodeelement->GetValue().Get();
03743       if(value) 
03744         {
03745           status = GEN_UI_COLORS.Add(name.Get(), value);                                                                       
03746         }
03747     }                  
03748     
03749   if(!nodeelement->GetName().Compare(__L("text"), true))
03750     {                                      
03751       value = nodeelement->GetValueAttribute(__L("name"));
03752       if(value) 
03753         {
03754           name = value;
03755         }
03756               
03757       value = nodeelement->GetValue().Get();
03758       if(value) 
03759         {
03760           status = GEN_UI_TEXTS.Add(name.Get(), value);                                                                       
03761         }
03762     }                      
03763   
03764   if(!nodeelement->GetName().Compare(__L("animation"), true))
03765     {                     
03766       value = nodeelement->GetValueAttribute(__L("name"));
03767       if(value)        
03768         { 
03769           name = value;
03770         }
03771               
03772       value = nodeelement->GetValue().Get();
03773       if(value) 
03774         {
03775           if(GetOrAddAnimationCache(name.Get(), value)) 
03776             {
03777               status = true;
03778             }
03779         }
03780     }
03781 
03782   if(recursive)
03783     {
03784       for(int c=0; c<nodeelement->GetNElements(); c++)
03785         {
03786           XFILEXMLELEMENT* subnodeelement = nodeelement->GetElement(c);
03787           if(subnodeelement) 
03788             {
03789               CreateCacheElements(subnodeelement, recursive);
03790             }
03791         }
03792     }
03793                   
03794   return status;
03795 }
03796 
03797 
03809 bool UI_MANAGER::RegisterEvents(bool active)
03810 {
03811   UI_XEVENT_TYPE eventtype[] = {  UI_XEVENT_TYPE_INPUT_CURSOR_MOVE            ,
03812                                   UI_XEVENT_TYPE_INPUT_CURSOR_MOTION          , 
03813                                   UI_XEVENT_TYPE_INPUT_SELECCTION             , 
03814                                   UI_XEVENT_TYPE_OUTPUT_SELECTED              ,
03815                                   UI_XEVENT_TYPE_OUTPUT_TEXTTOCHANGE          ,
03816                                   UI_XEVENT_TYPE_OUTPUT_CHANGECONTENTS      
03817                                };
03818 
03819   for(int c=0; c<(sizeof(eventtype) / sizeof(UI_XEVENT_TYPE)); c++)
03820     {
03821       if(active)        
03822              RegisterEvent(eventtype[c]);              
03823        else  DeRegisterEvent(eventtype[c]);              
03824     }
03825 
03826   return true;
03827 }
03828 
03829 
03843 UI_ELEMENT* UI_MANAGER::PreSelectElement(UI_ELEMENT* element, int x, int y)
03844 {
03845   UI_BOUNDARYLINE bline;
03846   bool            preselect         = false;
03847   bool            intofather        = false;
03848   UI_ELEMENT*     preselect_element = NULL;
03849   
03850   if(dynamic_cast<UI_PROPERTY_SELECTABLE*>(element))                                                                                                                                                                                                                                          
03851     {    
03852       if(element->IsActive() && element->IsVisible())
03853         {                                                                              
03854           bline.x       = element->GetXPositionWithScroll();          
03855           bline.y       = element->GetYPositionWithScroll();          
03856           bline.width   = element->GetBoundaryLine()->width;
03857           bline.height  = element->GetBoundaryLine()->height;
03858           
03859           preselect = bline.IsWithin(x, y);
03860           if(preselect)
03861             {
03862               if(element->IsSelected()) 
03863                 {
03864                   preselect = false;                        
03865                 }
03866             }
03867 
03868           element->SetPreSelect(preselect);                                                                           
03869           if(preselect) 
03870             {
03871               Elements_SetToRedraw(element);  
03872             
03873               last_xposition = x;
03874               last_yposition = y;
03875 
03876               preselect_element = element;
03877             }
03878         }
03879     } 
03880 
03881   if(!preselect)
03882     {
03883       bline.x       = element->GetXPosition();
03884       bline.y       = element->GetYPosition();
03885       bline.width   = element->GetBoundaryLine()->width;
03886       bline.height  = element->GetBoundaryLine()->height;
03887           
03888       intofather = bline.IsWithin(x, y);     
03889     }
03890 
03891   if(intofather)
03892     {                                                                                                                                     
03893       if(element->GetComposeElements()->GetSize())
03894         {                                                                      
03895           for(XDWORD d=0; d<element->GetComposeElements()->GetSize(); d++)   
03896             {
03897               UI_ELEMENT* subelement = element->GetComposeElements()->Get(d);
03898               if(subelement) 
03899                 {                  
03900                   preselect_element = PreSelectElement(subelement, x, y);                
03901                   if(preselect_element) break;  
03902                 }
03903             }
03904         }     
03905     }
03906 
03907   return preselect_element;
03908 }
03909 
03910 
03922 bool UI_MANAGER::SelectElement(UI_ELEMENT* element)
03923 {
03924   if(dynamic_cast<UI_PROPERTY_SELECTABLE*>(element))                                                                                                                                                                                                                                          
03925     {    
03926       if(SelectedElement(element)) return true;      
03927     } 
03928                                                                                                                                      
03929   if(element->GetComposeElements()->GetSize())
03930     {                                                                      
03931       for(XDWORD d=0; d<element->GetComposeElements()->GetSize(); d++)   
03932         {
03933           UI_ELEMENT* subelement = element->GetComposeElements()->Get(d);
03934           if(subelement) 
03935             { 
03936               if(dynamic_cast<UI_PROPERTY_SELECTABLE*>(subelement))                                                                                                                                                                                                                                          
03937                 {                  
03938                   if(SelectedElement(subelement)) return true;                  
03939                 }
03940             }
03941         }
03942     }                             
03943 
03944   return false;
03945 }
03946 
03947 
03959 bool UI_MANAGER::SelectedElement(UI_ELEMENT* element)
03960 {
03961   if(!element) return false;
03962 
03963   if(!element->IsPreSelect()) return false;
03964 
03965   UI_PROPERTY_SELECTABLE* element_selectable = dynamic_cast<UI_PROPERTY_SELECTABLE*>(element);
03966   if(!element_selectable) return false;
03967 
03968   XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("UI Element [%s]: Selected. "), element->GetName()->Get());
03969 
03970   if(dynamic_cast<UI_PROPERTY_EDITABLE*>(element))        
03971     {
03972       if(virtualkeyboard) virtualkeyboard->Show(true, element);
03973     }
03974 
03975   if(element->GetType() == UI_ELEMENT_TYPE_CHECKBOX)
03976     {
03977       UI_ELEMENT_CHECKBOX* element_checkbox = (UI_ELEMENT_CHECKBOX*)element;
03978       if(element_checkbox) 
03979         {
03980           bool status = element_checkbox->GetStatus();
03981 
03982           XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Checkbox select] name %s status: %s"), element_checkbox->GetName()->Get(), (status?__L("true"):__L("false"))); 
03983           
03984           status=!status;
03985 
03986           element_checkbox->SetStatus(status);
03987           
03988           XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Checkbox select] name %s status: %s"), element_checkbox->GetName()->Get(), (status?__L("true"):__L("false"))); 
03989 
03990           Elements_SetToRedraw(element);  
03991       }
03992     }
03993    else
03994     {
03995       if(virtualkeyboard)
03996         {
03997           if(virtualkeyboard->SelectInput(element)) 
03998             {
03999               return true;
04000             }
04001         }
04002 
04003       if(element->GetType() == UI_ELEMENT_TYPE_LISTBOX)
04004         {
04005           UI_ELEMENT_LISTBOX* element_listbox = (UI_ELEMENT_LISTBOX*)element;
04006           if(element_listbox)
04007             { 
04008               UI_ELEMENT_MENU* element_menu = element_listbox->Get_UIMenu();     
04009               if(element_menu)
04010                 {           
04011                   SetModalElement(element_menu);  
04012        
04013                   element_menu->SetVisible(!element_menu->IsVisible());     
04014                 }
04015             }
04016         }
04017       
04018       element->SetPreSelect(false);     
04019       element->SetSelected(true);
04020 
04021       element_selectable->GetXTimerSelected()->Reset();
04022                       
04023       SendEvent(UI_XEVENT_TYPE_OUTPUT_SELECTED, element); 
04024 
04025       //element->SetSelected(false);
04026       Elements_SetToRedraw(element);                   
04027     }
04028 
04029   return true;
04030 }                       
04031 
04032 
04044 bool UI_MANAGER::UnSelectedElement(UI_ELEMENT* element)
04045 {
04046   if(element->IsSelected()) 
04047     {
04048       UI_PROPERTY_SELECTABLE* element_selectable = dynamic_cast<UI_PROPERTY_SELECTABLE*>(element);
04049       if(!element_selectable) return false;
04050                         
04051       if(element_selectable->GetXTimerSelected())
04052         {
04053           if(element_selectable->GetXTimerSelected()->GetMeasureMilliSeconds() > element_selectable->GetTimeSelected()) 
04054             {             
04055               element->SetSelected(false);
04056               Elements_SetToRedraw(element);     
04057 
04058               GEN_USERINTERFACE.SendEvent(UI_XEVENT_TYPE_INPUT_CURSOR_MOVE, last_xposition, last_yposition);   
04059             }
04060         }                 
04061     }
04062                                                                                                                                      
04063   if(element->GetComposeElements()->GetSize())
04064     {                                                                      
04065       for(XDWORD d=0; d<element->GetComposeElements()->GetSize(); d++)   
04066         {
04067           UI_ELEMENT* subelement = element->GetComposeElements()->Get(d);
04068           if(subelement) 
04069             { 
04070               if(dynamic_cast<UI_PROPERTY_SELECTABLE*>(subelement))                                                                                                                                                                                                                                          
04071                 {                  
04072                   if(UnSelectedElement(subelement)) return true;                  
04073                 }
04074             }
04075         }
04076     }                             
04077 
04078   return false;
04079 }
04080 
04081 
04091 bool UI_MANAGER::UnSelectedElement()
04092 {
04093   for(int e=0; e<2; e++)
04094     {    
04095       UI_LAYOUT* layout = NULL;
04096 
04097       switch(e)
04098         {
04099           case  0 :  layout = Layouts_GetSelectedLayout();    break;
04100           case  1 :  layout = Layouts_GetInAllLayout();       break;
04101         }
04102                                                           
04103       if(!layout) break;
04104 
04105       for(XDWORD c=0; c<layout->Elements_Get()->GetSize(); c++)
04106         {
04107           UI_ELEMENT* element = layout->Elements_Get()->Get(c);
04108           if(element) UnSelectedElement(element);                        
04109         }        
04110     }
04111 
04112   return true;
04113 }
04114 
04115 
04128 bool UI_MANAGER::UseMotionInElement(UI_ELEMENT* element, INPCURSORMOTION* cursormotion)
04129 {
04130   if(!element)      return false;
04131   if(!cursormotion) return false;
04132 
04133   if(!element->IsVisible()) return false;
04134 
04135   UI_PROPERTY_SCROLLEABLE* property_scrolleable = dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element);
04136   if(!property_scrolleable) return false;
04137 
04138   bool isinrect = cursormotion->IsInRect((int)element->GetXPosition()           , (int)(element->GetYPosition() - element->GetBoundaryLine()->height), 
04139                                          (int)element->GetBoundaryLine()->width , (int)element->GetBoundaryLine()->height);
04140   if(isinrect)
04141     {
04142       XDWORD differential = 0;
04143       INPCURSORMOTION_DIR motiondir = cursormotion->GetDirectionByDifferential(&differential);
04144       if(motiondir != INPCURSORMOTION_DIR_UNKNOWN)
04145         {
04146           switch(motiondir)
04147             {
04148               case INPCURSORMOTION_DIR_UP         :
04149               case INPCURSORMOTION_DIR_DOWN       : { double shift = (differential/3);
04150                                                       if(motiondir == INPCURSORMOTION_DIR_DOWN) shift *= -1;
04151                                                       
04152                                                       property_scrolleable->Scroll_SetStep(UI_PROPERTY_SCROLLEABLE_TYPE_VERTICAL, shift);   
04153                                                       GEN_USERINTERFACE.Elements_SetToRedraw(element);                                                                                                                 
04154                                                                                                                                                                                                                                                                                           
04155                                                       XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("[Input Motion]  motion %s points: %d"), (motiondir == INPCURSORMOTION_DIR_UP)?__L("up"):__L("down"), cursormotion->GetNPoints());
04156 
04157                                                       return true;  
04158                                                     }
04159                                                     break;              
04160 
04161               case INPCURSORMOTION_DIR_RIGHT      : 
04162               case INPCURSORMOTION_DIR_LEFT       : break;                  
04163 
04164                                       default     : break;
04165             }             
04166         } 
04167     }
04168                                                                                                                                              
04169   if(element->GetComposeElements()->GetSize())
04170     {                                                                      
04171       for(XDWORD d=0; d<element->GetComposeElements()->GetSize(); d++)   
04172         {
04173           UI_ELEMENT* subelement = element->GetComposeElements()->Get(d);
04174           if(subelement) 
04175             { 
04176               if(dynamic_cast<UI_PROPERTY_SELECTABLE*>(subelement))                                                                                                                                                                                                                                          
04177                 {                  
04178                   if(UseMotionInElement(subelement, cursormotion)) return true;                  
04179                 }
04180             }
04181         }
04182     }                             
04183 
04184   return false;
04185 }
04186 
04187 
04199 bool UI_MANAGER::UseMotion(INPCURSORMOTION* cursormotion)
04200 {
04201   if(!cursormotion) return false;
04202 
04203   for(int e=0; e<2; e++)
04204     {    
04205       UI_LAYOUT* layout = NULL;
04206 
04207       switch(e)
04208         {
04209           case  0 :  layout = Layouts_GetSelectedLayout();    break;
04210           case  1 :  layout = Layouts_GetInAllLayout();       break;
04211         }
04212                                                           
04213       if(!layout) break;
04214 
04215       for(XDWORD c=0; c<layout->Elements_Get()->GetSize(); c++)
04216         {
04217           UI_ELEMENT* element = layout->Elements_Get()->Get(c);
04218           if(element) 
04219             {
04220               if(UseMotionInElement(element, cursormotion)) return true;
04221             }
04222         }        
04223     }
04224 
04225   return false;
04226 }
04227 
04228 
04241 void UI_MANAGER::HandleEvent_UI(UI_XEVENT* event)
04242 {
04243   switch(event->GetEventType())
04244     {
04245       case UI_XEVENT_TYPE_INPUT_CURSOR_MOVE       : { UI_ELEMENT*  _preselect_element = NULL;
04246                                                       int          x                  = event->GetXPos();
04247                                                       int          y                  = event->GetYPos();
04248 
04249                                                       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("x: %d, y: %d"), x, y);
04250                                                   
04251                                                       if(element_modal)
04252                                                         {                                                              
04253                                                            _preselect_element = PreSelectElement(element_modal, x, y);                                                              
04254                                                         }
04255                                                        else
04256                                                         {
04257                                                           for(int e=0; e<2; e++)
04258                                                             {    
04259                                                               UI_LAYOUT* layout = NULL;
04260 
04261                                                               switch(e)
04262                                                                 {
04263                                                                   case  0 :  layout = Layouts_GetSelectedLayout();    break;
04264                                                                   case  1 :  layout = Layouts_GetInAllLayout();       break;
04265                                                                 }
04266                                                           
04267                                                               if(!layout) break;
04268  
04269                                                                   for(XDWORD c=0; c<layout->Elements_Get()->GetSize(); c++)
04270                                                                     {
04271                                                                       UI_ELEMENT* element = layout->Elements_Get()->Get(c);
04272                                                                       if(element) 
04273                                                                         {
04274                                                                           _preselect_element = PreSelectElement(element, x, y);
04275                                                                           if(_preselect_element) break;                                                                       
04276                                                                         }
04277                                                                     }
04278 
04279                                                               if(_preselect_element) break;                                                                                                                        
04280                                                             }
04281                                                         }
04282  
04283                                                       if(!_preselect_element)  
04284                                                         {
04285                                                           if(preselect_element) ResetPreselect();                                                                                                 
04286                                                         } 
04287                                                        else
04288                                                         {
04289                                                           if(_preselect_element != preselect_element)
04290                                                             {
04291                                                               if(preselect_element) ResetPreselect();                                     
04292                                                               preselect_element = _preselect_element;                                                                      
04293                                                             }                                                           
04294                                                         }                                                                                                                                                  
04295                                                     }
04296                                                     break;
04297 
04298       case UI_XEVENT_TYPE_INPUT_CURSOR_MOTION     : { 
04299                                                       if(UseMotion(event->GetCursorMotion()))
04300                                                         {
04301                                                           UnSelectedElement();
04302                                                         }
04303                                                     }
04304                                                     break;
04305 
04306       case UI_XEVENT_TYPE_INPUT_SELECCTION        : { if(element_modal)
04307                                                         { 
04308                                                           if(xmutex_modal) xmutex_modal->Lock(); 
04309 
04310                                                           SelectElement(element_modal);                                                                                   
04311 
04312                                                           if(xmutex_modal) xmutex_modal->UnLock(); 
04313                                                         }
04314                                                        else
04315                                                         {
04316                                                           for(int e=0; e<2; e++)
04317                                                             {    
04318                                                               UI_LAYOUT* layout = NULL;
04319 
04320                                                               switch(e)
04321                                                                 {
04322                                                                   case  0 :  layout = Layouts_GetSelectedLayout();    break;
04323                                                                   case  1 :  layout = Layouts_GetInAllLayout();       break;
04324                                                                 }
04325 
04326                                                               if(layout)  
04327                                                                 {
04328                                                                   for(XDWORD c=0; c<layout->Elements_Get()->GetSize(); c++)
04329                                                                     {
04330                                                                       UI_ELEMENT* element = layout->Elements_Get()->Get(c);
04331                                                                       if(element) 
04332                                                                         {
04333                                                                           if(SelectElement(element)) break;                                                                                                                                                                                                                                                                                                                                      
04334                                                                         }
04335                                                                     }
04336                                                                 }
04337                                                             }
04338                                                         }
04339                                                     }
04340                                                     break;      
04341     }
04342 } 
04343 
04344 
04357 void UI_MANAGER::HandleEvent(XEVENT* xevent)
04358 {
04359  if(!xevent) return;
04360 
04361   switch(xevent->GetEventFamily())
04362     {
04363       case XEVENT_TYPE_USERINTERFACE    : { UI_XEVENT* event = (UI_XEVENT*)xevent;
04364                                             if(!event) return;
04365 
04366                                             HandleEvent_UI(event);
04367                                           }
04368                                           break;
04369     }
04370 }
