04391   skin_selected       = NULL;
04392 
04393   xmutex_modal        = NULL;
04394   element_modal       = NULL;
04395 
04396   xmutex_UIevent      = NULL;
04397 
04398   last_xposition      = 0;
04399   last_yposition      = 0;
04400 
04401 
04402   preselect_element   = NULL;
04403 
04404   virtualkeyboard     = NULL;
04405 }
