00538 UI_SKIN_TYPE UI_SKIN::GetType(XCHAR* name)
00539 {
00540   XSTRING _name;
00541 
00542   if(name) 
00543          _name = name;  
00544     else _name = this->name;
00545 
00546   if(_name.IsEmpty()) return UI_SKIN_TYPE_UNKNOWN;
00547 
00548   if(!_name.Compare(__L("flat"), true))       return UI_SKIN_TYPE_FLAT;
00549 
00550   return UI_SKIN_TYPE_UNKNOWN;
00551 }
