00777 bool UI_SKIN::CalculateBoundaryLine(UI_ELEMENT* element)
00778 {
00779   if(!element) return false;
00780 
00781   switch(element->GetType())
00782     {
00783       case UI_ELEMENT_TYPE_UNKNOWN              : return false;                                                     break;      
00784         case UI_ELEMENT_TYPE_SCROLL               : return CalculateBoundaryLine_Scroll(element);                     break;
00785         case UI_ELEMENT_TYPE_TEXT                     : return CalculateBoundaryLine_Text(element);                       break;    
00786       case UI_ELEMENT_TYPE_TEXTBOX            : return CalculateBoundaryLine_TextBox(element);                    break;    
00787         case UI_ELEMENT_TYPE_IMAGE              : return CalculateBoundaryLine_Image(element);                      break;
00788       case UI_ELEMENT_TYPE_ANIMATION            : return CalculateBoundaryLine_Animation(element);                  break;
00789       case UI_ELEMENT_TYPE_OPTION                   : return CalculateBoundaryLine_Option(element);                     break;
00790       case UI_ELEMENT_TYPE_MULTIOPTION      : return CalculateBoundaryLine_MultiOption(element);                break;
00791         case UI_ELEMENT_TYPE_BUTTON                 : return CalculateBoundaryLine_Button(element);                     break;              
00792       case UI_ELEMENT_TYPE_CHECKBOX             : return CalculateBoundaryLine_CheckBox(element);                   break;              
00793       case UI_ELEMENT_TYPE_EDITTEXT         : return CalculateBoundaryLine_EditText(element);                   break;        
00794       case UI_ELEMENT_TYPE_FORM                     : return CalculateBoundaryLine_Form(element);                       break; 
00795       case UI_ELEMENT_TYPE_MENU           : return CalculateBoundaryLine_Menu(element);                       break;                  
00796       case UI_ELEMENT_TYPE_LISTBOX        : return CalculateBoundaryLine_ListBox(element);                    break;                  
00797       case UI_ELEMENT_TYPE_PROGRESSBAR    : return CalculateBoundaryLine_ProgressBar(element);                break;                  
00798     }
00799 
00800   return false;
00801 }
