01054 bool UI_SKIN::CalculeBoundaryLine_AllElements(UI_ELEMENT* element, bool recursive)
01055 {
01056   CalculateBoundaryLine(element);
01057 
01058   for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
01059     {
01060       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
01061       if(subelement) CalculeBoundaryLine_AllElements(subelement, recursive);                   
01062     } 
01063 
01064   return true;
01065 }
