01081 bool UI_SKIN::GetAddPositionScrollSteps(UI_ELEMENT* element, double& x_positionwithscroll, double& y_positionwithscroll)
01082 {  
01083   if(!element) return false;
01084 
01085   UI_PROPERTY_SCROLLEABLE* property_scrolleable = dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element);
01086   if(property_scrolleable)
01087     {
01088       x_positionwithscroll += property_scrolleable->Scroll_GetDisplacement(UI_PROPERTY_SCROLLEABLE_TYPE_HORIZONTAL);   
01089       y_positionwithscroll += property_scrolleable->Scroll_GetDisplacement(UI_PROPERTY_SCROLLEABLE_TYPE_VERTICAL);   
01090     }
01091     
01092   if(element->GetFather()) GetAddPositionScrollSteps(element->GetFather(), x_positionwithscroll, y_positionwithscroll);      
01093     
01094   return true;
01095 }
