01129 bool UI_SKIN::Elements_SetToRedraw(UI_ELEMENT* element, bool recursive)
01130 {
01131   if(!element) return false;
01132 
01133   bool status = true;
01134 
01135   element->SetMustReDraw(true);
01136 
01137   if(recursive)
01138     {
01139       for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
01140         {
01141           UI_ELEMENT* subelement = (UI_ELEMENT*)element->GetComposeElements()->Get(c);
01142           if(subelement) 
01143             { 
01144               status = Elements_SetToRedraw(subelement, recursive);          
01145               
01146             } else break;
01147         }           
01148     }
01149 
01150   return status;
01151 }
