01166 bool UI_SKIN::Draw(UI_ELEMENT* element)  
01167 {
01168   if(!element) return false;
01169 
01170   if(!element->IsVisible())  return false;
01171 
01172   if(element->IsBlinking())
01173     {
01174       if(element->GetTimerBlink()->GetMeasureMilliSeconds() > element->IsBlinking()) 
01175         {
01176           element->SwicthStateBlink();                  
01177           Elements_SetToRedraw(element);
01178         }
01179        
01180       if(!element->GetStateBlink()) return false;        
01181     }
01182 
01183   bool status = false;
01184 
01185   switch(element->GetType())
01186     {
01187       case UI_ELEMENT_TYPE_UNKNOWN              : break;  
01188                              default      : break;
01189       case UI_ELEMENT_TYPE_SCROLL                 : status = Draw_Scroll(element);            break;
01190         case UI_ELEMENT_TYPE_TEXT                     : status = Draw_Text(element);              break; 
01191       case UI_ELEMENT_TYPE_TEXTBOX              : status = Draw_TextBox(element);           break;      
01192         case UI_ELEMENT_TYPE_IMAGE              : status = Draw_Image(element);             break;
01193       case UI_ELEMENT_TYPE_ANIMATION            : status = Draw_Animation(element);         break;
01194       case UI_ELEMENT_TYPE_OPTION                   : status = Draw_Option(element);            break;
01195       case UI_ELEMENT_TYPE_MULTIOPTION      : status = Draw_MultiOption(element);       break;
01196         case UI_ELEMENT_TYPE_BUTTON                 : status = Draw_Button(element);            break;              
01197       case UI_ELEMENT_TYPE_CHECKBOX             : status = Draw_CheckBox(element);          break;              
01198       case UI_ELEMENT_TYPE_EDITTEXT       : status = Draw_EditText(element);          break;          
01199       case UI_ELEMENT_TYPE_FORM                     : status = Draw_Form(element);              break;
01200       case UI_ELEMENT_TYPE_MENU           : status = Draw_Menu(element);              break;             
01201       case UI_ELEMENT_TYPE_LISTBOX        : status = Draw_ListBox(element);           break;             
01202       case UI_ELEMENT_TYPE_PROGRESSBAR    : status = Draw_ProgressBar(element);       break;                       
01203     }
01204 
01205   return status;
01206 } 
