00144 bool UI_SKINCANVAS_REBUILDAREAS::RebuildAllAreas()
00145 {   
00146   XDWORD max_z_level = 0;
00147   XDWORD nareas      = areas.GetSize();
00148   bool   first       =  false;
00149 
00150   if(!nareas) return true;
00151 
00152   for(XDWORD c=0; c<nareas; c++)
00153     {
00154       GRP2DREBUILDAREA* area = areas.Get(c);
00155       if(area)
00156         {
00157           UI_ELEMENT* element = (UI_ELEMENT*)area->GetExtraData();
00158           if(element)
00159             {
00160               max_z_level =  __MAX(max_z_level, element->GetZLevel()); 
00161             }
00162         }
00163     }
00164 
00165   if(!max_z_level) return false;
00166 
00167   for(XDWORD level = max_z_level; level>0; level--)  
00168     {
00169       nareas = areas.GetSize();  
00170 
00171       for(int index = nareas-1; index>=0; index--)  
00172         {
00173           GRP2DREBUILDAREA* area = areas.Get(index);
00174           if(area)
00175             {
00176               UI_ELEMENT* element = (UI_ELEMENT*)area->GetExtraData();
00177               if(element)
00178                 {
00179                   if(element->MustReDraw() || (!element->IsVisible())) 
00180                     { 
00181                       if(element->GetZLevel() == level)
00182                         {       
00183                           // XTRACE_PRINTCOLOR(XTRACE_COLOR_PURPLE, __L("Del area level [%d] [%s] "), element->GetZLevel(), element->GetName()->Get());
00184                           GRPBITMAP* bitmap = area->GetBitmap();              
00185                           if(bitmap) PutBitmapNoAlpha(area->GetXPos(), area->GetYPos(), bitmap);
00186 
00187                           areas.Delete(area);                
00188                           delete area;  
00189                         }              
00190                     }
00191                 }
00192             }           
00193         }
00194     }  
00195 
00196   return true;
00197 }
