00504 bool UI_SKINCANVAS::LoadFonts()
00505 {
00506   XPATH  xpath;
00507   bool   status = false; 
00508 
00509   GRPCANVAS* canvas = GetCanvas();
00510   if(!canvas) return false;
00511   
00512   if(!rasterfontname.IsEmpty())
00513     {
00514       status = canvas->RasterFont_Select(rasterfontname.Get());
00515 
00516     } else status = true;
00517 
00518   if(status)
00519     {
00520       if(!vectorfontname.IsEmpty())
00521         {          
00522           GEN_XPATHSMANAGER.GetPathOfSection(XPATHSMANAGERSECTIONTYPE_FONTS, xpath);
00523           xpath.Slash_Add();
00524           xpath.Add(vectorfontname.Get());
00525 
00526           if(GEN_USERINTERFACE.IsZippedFile())
00527             {             
00528               XFILEUNZIP* unzipfile = GEN_USERINTERFACE.GetUnzipFile();
00529               if(unzipfile)
00530                 {                  
00531                   XPATH pathnamefilecmp;
00532 
00533                   pathnamefilecmp = APPDEFAULT_DIRECTORY_FONTS;
00534                   pathnamefilecmp.Slash_Add();
00535                   pathnamefilecmp += vectorfontname;
00536 
00537                   status = unzipfile->DecompressFile(pathnamefilecmp, (*GEN_USERINTERFACE.GetUnzipPathFile()), vectorfontname.Get());   
00538                   if(status)
00539                     {  
00540                       XPATH unzippathfile_tmp;
00541 
00542                       unzippathfile_tmp  = GEN_USERINTERFACE.GetUnzipPathFile()->Get();
00543                       unzippathfile_tmp += vectorfontname;
00544 
00545                       status = canvas->VectorFont_Load(unzippathfile_tmp);  
00546 
00547                       //GEN_USERINTERFACE.DeleteTemporalUnZipFile(unzippathfile_tmp);  
00548 
00549                       fontpathfile = unzippathfile_tmp;
00550                     }
00551                     
00552                 }
00553             }
00554            else
00555             {                            
00556               status = canvas->VectorFont_Load(xpath);    
00557             }
00558 
00559         } else status = true;
00560     }
00561 
00562   return status;
00563 }
