00575 bool UI_SKINCANVAS::Background_LoadBitmap()
00576 {
00577   if(!screen) return false;
00578 
00579   XPATH           xpath;
00580   GRPBITMAPFILE*  bitmapfile;
00581   bool            status = false;
00582 
00583   bitmapfile = new GRPBITMAPFILE();
00584   if(!bitmapfile) 
00585     {
00586       return status;
00587     }
00588 
00589   GEN_XPATHSMANAGER.GetPathOfSection(XPATHSMANAGERSECTIONTYPE_GRAPHICS, xpath);
00590   xpath.Slash_Add();
00591   xpath.Add(background_namefile.Get());
00592 
00593   if(GEN_USERINTERFACE.IsZippedFile())
00594     {
00595       XFILEUNZIP* unzipfile = GEN_USERINTERFACE.GetUnzipFile();
00596       if(unzipfile)
00597         {                
00598           XPATH pathnamefilecmp;
00599 
00600           pathnamefilecmp = APPDEFAULT_DIRECTORY_GRAPHICS;
00601           pathnamefilecmp.Slash_Add();
00602           pathnamefilecmp += background_namefile;
00603  
00604           status = unzipfile->DecompressFile(pathnamefilecmp, (*GEN_USERINTERFACE.GetUnzipPathFile()), background_namefile.Get());   
00605           if(status)
00606             {  
00607               XPATH unzippathfile_tmp;
00608 
00609               unzippathfile_tmp  = GEN_USERINTERFACE.GetUnzipPathFile()->Get();
00610               unzippathfile_tmp += background_namefile;
00611 
00612               background_bitmap = bitmapfile->Load(unzippathfile_tmp, screen->GetMode());
00613               if(background_bitmap) 
00614                 {            
00615                   status = true;
00616                 }
00617 
00618               GEN_USERINTERFACE.DeleteTemporalUnZipFile(unzippathfile_tmp);  
00619             }
00620         }
00621     }
00622    else
00623     {                                          
00624       background_bitmap = bitmapfile->Load(xpath, screen->GetMode());
00625       if(background_bitmap) 
00626         {            
00627           status = true;
00628         }
00629     }
00630 
00631   delete bitmapfile;
00632 
00633   return status;
00634 
00635 }
