00651 bool UI_SKINCANVAS::GetFatherSize(UI_ELEMENT* element, double& width, double& height)
00652 {
00653   GRPCANVAS* canvas = GetCanvas();
00654   if(!canvas) return false;
00655 
00656   width  = 0.0f;
00657   height = 0.0f;
00658   
00659   if(element->GetFather())  
00660     {
00661       width  = element->GetFather()->GetBoundaryLine()->width; 
00662       height = element->GetFather()->GetBoundaryLine()->height; 
00663     }
00664    else  
00665     { 
00666       width  = canvas->GetWidth()-1;   
00667       height = canvas->GetHeight()-1;   
00668     }
00669 
00670   return true;
00671 }
