00685 double UI_SKINCANVAS::GetPositionWithoutDefine(double position)
00686 { 
00687   switch((int)position)
00688     {
00689       case UI_ELEMENT_TYPE_ALIGN_LEFT     : break;
00690       case UI_ELEMENT_TYPE_ALIGN_RIGHT      : break;
00691       case UI_ELEMENT_TYPE_ALIGN_UP       : break;
00692       case UI_ELEMENT_TYPE_ALIGN_DOWN       : break;
00693       case UI_ELEMENT_TYPE_ALIGN_CENTER     : break;
00694                         default       : return position;
00695     } 
00696     
00697   return 0.0f;
