00712 bool UI_SKINCANVAS::SetAroundFromSubElements(UI_ELEMENT* element)
00713 {
00714   if(!element) return false;
00715   if(element->GetComposeElements()->IsEmpty()) return false;
00716 
00717   UI_BOUNDARYLINE bondaryline[2];
00718 
00719   bondaryline[1].x      = element->GetXPosition();
00720   bondaryline[1].y      = element->GetYPosition();
00721   bondaryline[1].width  = element->GetBoundaryLine()->width;
00722   bondaryline[1].height = element->GetBoundaryLine()->height;
00723 
00724   for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
00725     {
00726       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
00727       if(subelement)
00728         {
00729           bondaryline[0].x       = subelement->GetXPosition();
00730           bondaryline[0].y       = subelement->GetYPosition();
00731           bondaryline[0].width   = subelement->GetBoundaryLine()->width;
00732           bondaryline[0].height  = subelement->GetBoundaryLine()->height;
00733 
00734           bondaryline[1].SetAround(bondaryline[0]);
00735         }
00736     }
00737 
00738 
00739   for(int c=(int)element->GetComposeElements()->GetSize()-1; c>=0; c--)
00740     {
00741       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
00742       if(subelement)
00743         {
00744           bondaryline[0].x       = subelement->GetXPosition();
00745           bondaryline[0].y       = subelement->GetYPosition();
00746           bondaryline[0].width   = subelement->GetBoundaryLine()->width;
00747           bondaryline[0].height  = subelement->GetBoundaryLine()->height;
00748 
00749           bondaryline[1].SetAround(bondaryline[0]);
00750         }
00751     }
00752  
00753   element->SetXPosition(bondaryline[1].x);
00754   element->SetYPosition(bondaryline[1].y);
00755   element->GetBoundaryLine()->width  = bondaryline[1].width;
00756   element->GetBoundaryLine()->height = bondaryline[1].height;
00757 
00758   return true;
00759 }
