00804 bool UI_SKINCANVAS::CalculePosition(UI_ELEMENT* element, double fatherwidth, double fatherheight)
00805 { 
00806   if(!element) return false;
00807 
00808   double x_position = 0.0f;
00809   double y_position = 0.0f;
00810 
00811   if(element->GetFather())
00812     {
00813       x_position = GetPositionWithoutDefine(element->GetFather()->GetXPosition());
00814       y_position = GetPositionWithoutDefine(element->GetFather()->GetYPosition());     
00815     }
00816        
00817   switch((int)element->GetBoundaryLine()->x)
00818     {
00819       case UI_ELEMENT_TYPE_ALIGN_LEFT     : x_position += 0;                                                                  break;
00820       case UI_ELEMENT_TYPE_ALIGN_RIGHT      : x_position += (fatherwidth - element->GetBoundaryLine()->width);                  break;
00821       case UI_ELEMENT_TYPE_ALIGN_CENTER   : x_position += (int)round((fatherwidth - element->GetBoundaryLine()->width)/2);    break;
00822                                 default   : x_position += GetPositionWithoutDefine(element->GetBoundaryLine()->x);            break;
00823     }
00824 
00825   if(element->GetFather())
00826     {
00827       switch((int)element->GetBoundaryLine()->y)
00828         {
00829           case UI_ELEMENT_TYPE_ALIGN_UP       : y_position -= fatherheight -element->GetBoundaryLine()->height;                   break;
00830           case UI_ELEMENT_TYPE_ALIGN_DOWN       : y_position -= 0;                                                                  break;
00831           case UI_ELEMENT_TYPE_ALIGN_CENTER     : y_position -= (int)round((fatherheight - element->GetBoundaryLine()->height)/2);  break;
00832                                     default   : y_position -= GetPositionWithoutDefine(element->GetBoundaryLine()->y);            break;
00833         }
00834      
00835     }
00836    else 
00837     {
00838       switch((int)element->GetBoundaryLine()->y)
00839         {
00840           case UI_ELEMENT_TYPE_ALIGN_UP       : y_position += element->GetBoundaryLine()->height;                                 break;
00841           case UI_ELEMENT_TYPE_ALIGN_DOWN       : y_position += fatherheight;                                                       break;
00842           case UI_ELEMENT_TYPE_ALIGN_CENTER     : y_position += (int)round((fatherheight + element->GetBoundaryLine()->height)/2);  break;
00843                                     default   : y_position += GetPositionWithoutDefine(element->GetBoundaryLine()->y);            break;
00844         }
00845     }
00846 
00847   x_position += (element->GetMargin(UI_ELEMENT_TYPE_ALIGN_LEFT) - element->GetMargin(UI_ELEMENT_TYPE_ALIGN_RIGHT));
00848   y_position += (element->GetMargin(UI_ELEMENT_TYPE_ALIGN_UP)   - element->GetMargin(UI_ELEMENT_TYPE_ALIGN_DOWN));
00849 
00850   element->SetXPosition(x_position);
00851   element->SetYPosition(y_position);
00852 
00853   return true;
00854 }
