00933 bool UI_SKINCANVAS::CalculateBoundaryLine_Text(UI_ELEMENT* element)
00934 {
00935   if(!element) return false;
00936 
00937   UI_ELEMENT_TEXT* element_text = (UI_ELEMENT_TEXT*)element;
00938   GRPCANVAS*       canvas       = GetCanvas();
00939 
00940   if(!canvas) return false;
00941   
00942   canvas->Vectorfont_GetConfig()->SetSize(element_text->GetSizeFont());
00943 
00944   XSTRING text;
00945   if(!element_text->GetText()->IsEmpty()) 
00946     {
00947       text = element_text->GetText()->Get();    
00948     }
00949    else 
00950     {
00951       if(!element_text->GetMaskText()->IsEmpty()) 
00952         {
00953           text = element_text->GetMaskText()->Get();
00954         }
00955     }
00956 
00957   double fatherwidth  = 0.0f;
00958   double fatherheight = 0.0f;
00959   
00960   GetFatherSize(element, fatherwidth, fatherheight);
00961 
00962   XSTRING string_max = text;
00963   if(element_text->GetMaxSizeText())
00964     {
00965       XDWORD size_max = element_text->GetMaxSizeText();     
00966       
00967       if(!size_max) size_max = text.GetSize();
00968       string_max.Empty();
00969       for(XDWORD c=0; c<size_max; c++)
00970         {
00971           string_max += __L("A");
00972         }
00973     }
00974 
00975   switch((int)element->GetBoundaryLine()->width)
00976     {
00977       case UI_ELEMENT_TYPE_ALIGN_AUTO    :              
00978       case                           0 : element->GetBoundaryLine()->width  = (double)canvas->VectorFont_GetWidth(string_max.Get());    break;
00979       case UI_ELEMENT_TYPE_ALIGN_MAX   : element->GetBoundaryLine()->width = fatherwidth;                                               break;      
00980     }
00981    
00982   switch((int)element->GetBoundaryLine()->height)
00983     {
00984       case UI_ELEMENT_TYPE_ALIGN_AUTO    :              
00985       case                           0 : element->GetBoundaryLine()->height  = (double)canvas->VectorFont_GetHeight(string_max.Get());  break;
00986       case UI_ELEMENT_TYPE_ALIGN_MAX   : element->GetBoundaryLine()->height  = fatherheight;                                            break;      
00987     }
00988 
00989   return CalculePosition(element, fatherwidth, fatherheight);
00990 }
