01013 
01014   switch((int)element->GetBoundaryLine()->width)
01015     {
01016       case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01017       case                            0 : 
01018       case UI_ELEMENT_TYPE_ALIGN_MAX    : return false; 
01019                                           break;
01020     }
01021    
01022   switch((int)element->GetBoundaryLine()->height)
01023     {
01024       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01025       case                            0 : 
01026       case UI_ELEMENT_TYPE_ALIGN_MAX    : return false; 
01027                                           break;
01028     }
01029 
