01045 bool UI_SKINCANVAS::CalculateBoundaryLine_Image(UI_ELEMENT* element)
01046 {
01047   if(!element) return false;
01048   
01049   UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element;
01050   if(!element_image->GetImage()) return false;
01051 
01052   double fatherwidth  = 0.0f;
01053   double fatherheight = 0.0f;
01054   
01055   GetFatherSize(element, fatherwidth, fatherheight);
01056 
01057   switch((int)element->GetBoundaryLine()->width)
01058     {
01059       case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01060       case                            0 : element->GetBoundaryLine()->width  = (double)element_image->GetImage()->GetWidth();       break;
01061       case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->width = fatherwidth;                                          break;      
01062     }
01063    
01064   switch((int)element->GetBoundaryLine()->height)
01065     {
01066       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01067       case                            0 : element->GetBoundaryLine()->height  = (double)element_image->GetImage()->GetHeight();     break;
01068       case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->height  = fatherheight;                                       break;      
01069     }
01070 
01071   return CalculePosition(element, fatherwidth, fatherheight);
01072 }
