01086 bool UI_SKINCANVAS::CalculateBoundaryLine_Animation(UI_ELEMENT* element)
01087 {
01088   UI_ELEMENT_ANIMATION* element_animation = (UI_ELEMENT_ANIMATION*)element;
01089   if(!element_animation) return false;  
01090 
01091   double fatherwidth  = 0.0f;
01092   double fatherheight = 0.0f;
01093   
01094   GetFatherSize(element, fatherwidth, fatherheight);
01095 
01096   switch((int)element->GetBoundaryLine()->width)
01097     {
01098       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01099       case                             0  : { double maximgwidth = 0.0f; 
01100 
01101                                               for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01102                                                 {
01103                                                  UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01104                                                  if(element_image) maximgwidth = __MAX((GetPositionWithoutDefine(element_image->GetBoundaryLine()->x) + element_image->GetBoundaryLine()->width), maximgwidth);                                                    
01105                                                 }
01106 
01107                                               element->GetBoundaryLine()->width = maximgwidth;
01108                                             }
01109                                             break;
01110 
01111       case UI_ELEMENT_TYPE_ALIGN_MAX      : element->GetBoundaryLine()->width = fatherwidth;                                          
01112                                             break;      
01113     }
01114    
01115   switch((int)element->GetBoundaryLine()->height)
01116     {
01117       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01118       case                             0  : { double maximgheight = 0.0f; 
01119 
01120                                               for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01121                                                 {
01122                                                  UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01123                                                  if(element_image) maximgheight = __MAX((GetPositionWithoutDefine(element_image->GetBoundaryLine()->y) + element_image->GetBoundaryLine()->height), maximgheight);                                                    
01124                                                 }
01125 
01126                                               element->GetBoundaryLine()->height = maximgheight;
01127                                             }
01128                                             break;
01129 
01130       case UI_ELEMENT_TYPE_ALIGN_MAX      : element->GetBoundaryLine()->height  = fatherheight;                                       
01131                                             break;      
01132     }
01133 
01134   CalculePosition(element, fatherwidth, fatherheight);
01135 
01136   for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01137     {
01138       UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01139       if(element_image)
01140         {
01141           CalculePosition(element_image, element_animation->GetBoundaryLine()->width, element_image->GetBoundaryLine()->height);
01142         }
01143     }
01144 
01145   return true;
01146 }
