01148 
01160 bool UI_SKINCANVAS::CalculateBoundaryLine_Option(UI_ELEMENT* element)
01161 {
01162   UI_ELEMENT_OPTION* element_option = (UI_ELEMENT_OPTION*)element;
01163   if(!element_option) return false;  
01164 
01165   double fatherwidth  = 0.0f;
01166   double fatherheight = 0.0f;
01167   
01168   GetFatherSize(element, fatherwidth, fatherheight);
01169 
01170   UI_ELEMENT_ANIMATION* element_animation = (UI_ELEMENT_ANIMATION*)element_option->Get_UIAnimation();
01171   UI_ELEMENT_TEXT*      element_text      = (UI_ELEMENT_TEXT*)element_option->Get_UIText();
01172 
01173   double maximgwidth  = 0.0f; 
01174   double maximgheight = 0.0f; 
01175 
01176   if(element_animation || element_text)
01177     {
01178       switch((int)element->GetBoundaryLine()->width)
01179         {
01180           case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01181           case                           0  : { switch(element_option->GetAllocationTextType())
01182                                                   {                                            
01183                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : 
01184                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation) maximgwidth += element_animation->GetBoundaryLine()->width;  
01185                                                                                                           if(element_text)      maximgwidth += element_text->GetBoundaryLine()->width;  
01186                                                                                                           break;
01187 
01188                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    :                                                                  
01189                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : 
01190                                                                                               default   : if(element_animation) maximgwidth = __MAX(maximgwidth, element_animation->GetBoundaryLine()->width);                                                                                                    
01191                                                                                                           if(element_text)      maximgwidth = __MAX(maximgwidth, element_text->GetBoundaryLine()->width);                                                                                                  
01192                                                                                                           break; 
01193                                                   } 
01194                                      
01195                                                 element->GetBoundaryLine()->width = maximgwidth;
01196                                               }
01197                                               break;
01198 
01199           case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->width = fatherwidth;                                          
01200                                               break;        
01201         }
01202 
01203       switch((int)element->GetBoundaryLine()->height)
01204         {
01205           case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01206           case                           0  : { switch(element_option->GetAllocationTextType())
01207                                                   {                                           
01208                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : 
01209                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation) maximgheight += element_animation->GetBoundaryLine()->height;  
01210                                                                                                           if(element_text)      maximgheight += element_text->GetBoundaryLine()->height;  
01211                                                                                                           break;
01212 
01213                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : 
01214                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : 
01215                                                                                               default   : if(element_animation) maximgheight = __MAX(maximgheight, element_animation->GetBoundaryLine()->height);                                                                                                    
01216                                                                                                           if(element_text)      maximgheight = __MAX(maximgheight, element_text->GetBoundaryLine()->height);                                                                                                  
01217                                                                                                           break; 
01218                                                   }                                      
01219                                                 element->GetBoundaryLine()->height = maximgheight;
01220                                               }
01221                                               break;
01222 
01223           case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->height  = fatherheight;                                       
01224                                               break;        
01225         }
01226     }
01227   
01228   CalculePosition(element_option, fatherwidth, fatherheight);
01229 
01230   if(element_animation) CalculePosition(element_animation, element_option->GetBoundaryLine()->width, element_option->GetBoundaryLine()->height);
01231   if(element_text)      CalculePosition(element_text     , element_option->GetBoundaryLine()->width, element_option->GetBoundaryLine()->height);  
01232 
01233   switch(element_option->GetAllocationTextType())
01234     {
01235       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : break;
01236 
01237       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation && element_text) element_animation->SetYPosition(element_animation->GetYPosition() - element_text->GetBoundaryLine()->height);     break;
01238       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : if(element_animation && element_text) element_text->SetYPosition(element_text->GetYPosition() - element_animation->GetBoundaryLine()->height);          break;                                                           
01239       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : if(element_animation && element_text) element_text->SetXPosition(element_text->GetXPosition() + element_animation->GetBoundaryLine()->width);           break;
01240       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation && element_text) element_animation->SetXPosition(element_animation->GetXPosition() + element_text->GetBoundaryLine()->width);      break;
01241       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : break; 
01242     } 
01243 
01244   if(element_animation)
01245     {
01246       for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01247         {
01248           UI_ELEMENT* subelement = element_animation->GetComposeElements()->Get(c);
01249           if(subelement) CalculePosition(subelement, element_animation->GetBoundaryLine()->width, element_animation->GetBoundaryLine()->height);         
01250         } 
01251     }
01252 
01253   SetAroundFromSubElements(element_option);
01254 
01255   return true;
01256 }
