01369 bool UI_SKINCANVAS::CalculateBoundaryLine_EditText(UI_ELEMENT* element)
01370 {  
01371   if(!element) return false;
01372 
01373   UI_ELEMENT_EDITTEXT* element_edittext = (UI_ELEMENT_EDITTEXT*)element;
01374   GRPCANVAS*           canvas           = GetCanvas();
01375 
01376   if(!canvas) return false;
01377   
01378   canvas->Vectorfont_GetConfig()->SetSize(element_edittext->GetSizeFont());
01379 
01380   XSTRING text;
01381   if(!element_edittext->GetText()->IsEmpty()) text = element_edittext->GetText()->Get();
01382     else if(!element_edittext->GetMaskText()->IsEmpty()) text = element_edittext->GetMaskText()->Get();
01383 
01384   double fatherwidth  = 0.0f;
01385   double fatherheight = 0.0f;
01386   
01387   GetFatherSize(element, fatherwidth, fatherheight);
01388 
01389   XSTRING string_max;
01390   XDWORD  size_max   =  element_edittext->GetMaxSizeText();
01391   
01392   if(!size_max) size_max = text.GetSize();
01393   for(XDWORD c=0; c<size_max; c++)
01394     {
01395       string_max += __L("A");
01396     }
01397 
01398   switch((int)element->GetBoundaryLine()->width)
01399     {
01400       case UI_ELEMENT_TYPE_ALIGN_AUTO :             
01401       case                          0 : element->GetBoundaryLine()->width  = (double)canvas->VectorFont_GetWidth(string_max.Get());     break;
01402       case UI_ELEMENT_TYPE_ALIGN_MAX  : element->GetBoundaryLine()->width = fatherwidth;                                                break;      
01403     }
01404    
01405   switch((int)element->GetBoundaryLine()->height)
01406     {
01407       case UI_ELEMENT_TYPE_ALIGN_AUTO :             
01408       case                          0 : element->GetBoundaryLine()->height  = (double)canvas->VectorFont_GetHeight(string_max.Get());   break;
01409       case UI_ELEMENT_TYPE_ALIGN_MAX  : element->GetBoundaryLine()->height  = fatherheight;                                             break;      
01410     }
01411 
01412   return CalculePosition(element, fatherwidth, fatherheight);
01413 }
