01644   if(element_progressrect)  CalculePosition(element_progressrect  , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);
01645   if(element_animation)     CalculePosition(element_animation     , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);
01646   if(element_text)          CalculePosition(element_text          , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);  
01647 
01648 
01649   switch(element_progressbar->GetAllocationTextType())
01650     {
01651       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : break;
01652 
01653       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation && element_text)     element_animation->SetYPosition(element_animation->GetYPosition() - element_text->GetBoundaryLine()->height);     
01654                                                             if(element_progressrect && element_text)  element_progressrect->SetYPosition(element_progressrect->GetYPosition() - (element_text->GetBoundaryLine()->height + 6));     
01655                                                             break;
01656 
01657       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : if(element_animation && element_text)     element_text->SetYPosition(element_text->GetYPosition() - element_animation->GetBoundaryLine()->height);          
01658                                                             if(element_progressrect && element_text)  element_text->SetYPosition(element_text->GetYPosition() - (element_progressrect->GetBoundaryLine()->height + 6));          
01659                                                             break;                                                           
01660 
01661       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : if(element_animation && element_text)     element_text->SetXPosition(element_text->GetXPosition() + element_animation->GetBoundaryLine()->width);           
01662                                                             if(element_progressrect && element_text)  element_text->SetXPosition(element_text->GetXPosition() + element_progressrect->GetBoundaryLine()->width);    
01663                                                             break;
01664 
01665       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation && element_text)     element_animation->SetXPosition(element_animation->GetXPosition() + element_text->GetBoundaryLine()->width);      
01666                                                             if(element_progressrect && element_text)  element_progressrect->SetXPosition(element_progressrect->GetXPosition() + element_text->GetBoundaryLine()->width);    
01667                                                             break;
01668 
01669       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : if(element_progressrect && element_text)  
01670                                                               {
01671                                                                 element_text->SetXPosition(element_progressrect->GetXPosition() + ((element_progressrect->GetBoundaryLine()->width  - element_text->GetBoundaryLine()->width)/2));    
01672                                                                 element_text->SetYPosition(element_progressrect->GetYPosition() - ((element_progressrect->GetBoundaryLine()->height - element_text->GetBoundaryLine()->height)/2));             
01673                                                               }
01674                                                             break; 
01675     } 
01676 
01677   if(element_animation)
01678     {
01679       for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01680         {
01681           UI_ELEMENT* subelement = element_animation->GetComposeElements()->Get(c);
01682           if(subelement) CalculePosition(subelement, element_animation->GetBoundaryLine()->width, element_animation->GetBoundaryLine()->height);         
01683         } 
01684     }
01685 
01686   SetAroundFromSubElements(element_progressbar);
01687 
01688   return true;
01689 }
01690 
