01705 bool UI_SKINCANVAS::SetElementPosition(UI_ELEMENT* element, double x_position, double y_position)
01706 {
01707   if(!element) return false;
01708 
01709   element->GetBoundaryLine()->x = x_position;
01710   element->GetBoundaryLine()->y = y_position;
01711 
01712   bool status = CalculeBoundaryLine_AllElements(element);
01713 
01714   if(dynamic_cast<UI_ELEMENT_FORM*>(element))                                                                                                                                                                                                                                          
01715     {                                                     
01716       UI_ELEMENT_FORM* element_form = (UI_ELEMENT_FORM*)element;
01717 
01718       element_form->GetVisibleRect()->x = element_form->GetXPosition();
01719       element_form->GetVisibleRect()->y = element_form->GetYPosition();
01720     }
01721 
01722   return status;
01723 }
