01755 bool UI_SKINCANVAS::Draw_Text(UI_ELEMENT* element)  
01756 {
01757   if(!element) return false;  
01758 
01759   UI_ELEMENT_TEXT*    element_text  = (UI_ELEMENT_TEXT*)element;
01760   GRPCANVAS*          canvas        = GetCanvas();  
01761   double              x_position    = 0.0f;
01762   double              y_position    = 0.0f;  
01763   XRECT               clip_rect;                       
01764   
01765   if(!canvas) return false;
01766  
01767   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
