01772                               element->GetColor()->GetGreen(),
01773                               element->GetColor()->GetBlue(),
01774                               element->GetColor()->GetAlpha());
01775                          
01776       
01777       canvas->Vectorfont_GetConfig()->SetColor(&color);
01778       canvas->Vectorfont_GetConfig()->SetSize(element_text->GetSizeFont());  
01779       canvas->VectorFont_Print(x_position, y_position, element_text->GetText()->Get());
01780     }
01781 
