01787 
01800 bool UI_SKINCANVAS::Draw_TextBox(UI_ELEMENT* element)  
01801 { 
01802   if(!element) return false;  
01803 
01804   UI_ELEMENT_TEXTBOX* element_textbox = (UI_ELEMENT_TEXTBOX*)element;
01805   GRPCANVAS*          canvas          = GetCanvas();  
01806   double              x_position      = 0.0f;
01807   double              y_position      = 0.0f;  
01808   XRECT               clip_rect;                       
01809   
01810   if(!canvas) return false;
01811  
01812   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01813       
01814   if(element->MustReDraw()) 
01815     {
01816       GRP2DCOLOR_RGBA8                color(element->GetColor()->GetRed(),
01817                                             element->GetColor()->GetGreen(),
01818                                             element->GetColor()->GetBlue(),
01819                                             element->GetColor()->GetAlpha());
01820       XVECTOR<UI_SKIN_TEXTBOX_PART*>  parts;
01821       XVECTOR<UI_SKIN_TEXTBOX_IMAGE*> images;
01822 
01823                                
01824       canvas->Vectorfont_GetConfig()->SetColor(&color);
01825       canvas->Vectorfont_GetConfig()->SetSize(element_textbox->GetSizeFont());  
01826 
01827       TextBox_GenerateLines(element_textbox, canvas, x_position, y_position, parts, images);
01828 
01829       for(XDWORD c=0; c<parts.GetSize(); c++)
01830         {
01831           UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
01832           if(textbox_part)
01833             {
01834               GRP2DCOLOR_RGBA8 color_part(textbox_part->GetColor()->GetRed(),
01835                                           textbox_part->GetColor()->GetGreen(),
01836                                           textbox_part->GetColor()->GetBlue(),
01837                                           textbox_part->GetColor()->GetAlpha());
01838 
01839               canvas->Vectorfont_GetConfig()->SetColor(&color_part);
01840 
01841               canvas->VectorFont_Printf(textbox_part->GetXPos(), textbox_part->GetYPos(), textbox_part->GetText()->Get());   
01842 
01843 
01844               #ifdef USERINTERFACE_DEBUG 
01845               GRP2DCOLOR_RGBA8  color_debug(255, 0, 255);
01846 
01847               int width   = (int)canvas->VectorFont_GetWidth(textbox_part->GetText()->Get());
01848               int height  = (int)canvas->VectorFont_GetHeight(__L("A"));
01849 
01850               canvas->SetLineWidth(1.0f);
01851               canvas->SetLineColor(&color_debug);
01852               canvas->Rectangle(textbox_part->GetXPos() ,  
01853                                 textbox_part->GetYPos() ,
01854                                 textbox_part->GetXPos() + width , 
01855                                 textbox_part->GetYPos() - height);             
01856               #endif  
01857             }
01858         } 
01859 
01860      for(XDWORD c=0; c<images.GetSize(); c++)
01861         {
01862           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(c);
01863           if(textbox_image)
01864             {
01865               canvas->PutBitmapAlpha(textbox_image->GetBoundaryLine()->x  ,
01866                                      textbox_image->GetBoundaryLine()->y  ,
01867                                      textbox_image->GetAnimation()->GetBitmap(), 100);
01868 
01869               #ifdef USERINTERFACE_DEBUG               
01870               GRP2DCOLOR_RGBA8  color_debug(0, 0, 255);
01871 
01872               canvas->SetLineWidth(1.0f);
01873               canvas->SetLineColor(&color_debug);
01874               canvas->Rectangle(textbox_image->GetBoundaryLine()->x ,  
01875                                 textbox_image->GetBoundaryLine()->y ,
01876                                 textbox_image->GetBoundaryLine()->x + textbox_image->GetBoundaryLine()->width, 
01877                                 textbox_image->GetBoundaryLine()->y + textbox_image->GetBoundaryLine()->height);              
01878               #endif
01879             }
01880         }
01881    
01882       parts.DeleteContents();
01883       parts.DeleteAll();
01884 
01885       images.DeleteContents();
01886       images.DeleteAll();
01887     }
01888 
01889   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01890   
01891   return true;
01892 }
