01894   
01907 bool UI_SKINCANVAS::Draw_Image(UI_ELEMENT* element)  
01908 {
01909   if(!element) return false;
01910  
01911   UI_ELEMENT_IMAGE*   element_image = (UI_ELEMENT_IMAGE*)element;
01912   GRPCANVAS*          canvas        = GetCanvas(); 
01913   double              x_position    = 0.0f;
01914   double              y_position    = 0.0f;
01915   XRECT               clip_rect;                       
01916 
01917   if(!element_image->GetImage()) return false; 
01918   if(!canvas) return false;
01919 
01920   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01921 
01922   if(element->MustReDraw()) 
01923     {
01924       canvas->PutBitmapAlpha(x_position , 
01925                              y_position - element_image->GetImage()->GetHeight(), 
01926                              element_image->GetImage(), element_image->GetAlpha());
01927     }
01928 
01929   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01930     
01931   return true;
01932 }
