01934 
01947 bool UI_SKINCANVAS::Draw_Animation(UI_ELEMENT* element)  
01948 {
01949   if(!element) return false;
01950 
01951   UI_ELEMENT_ANIMATION* element_animation  = (UI_ELEMENT_ANIMATION*)element;  
01952   UI_ELEMENT_IMAGE*     element_image      = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(element_animation->IndexImage_Get());
01953   double                x_position         = 0.0f;
01954   double                y_position         = 0.0f;  
01955   GRPCANVAS*            canvas             = GetCanvas();    
01956   XRECT                 clip_rect;          
01957   bool                  redraw             = false;
01958 
01959   if(!element_animation)  return false;
01960   if(!element_image)      return false;
01961 
01962   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01963 
01964   if(element->MustReDraw()) 
01965     {
01966       if(element_animation)
01967         {
01968           XBYTE alpha = element_animation->GetAlpha();
01969           if(alpha < element_image->GetAlpha()) element_image->SetAlpha(alpha);
01970         }
01971 
01972       Draw(element_image);     
01973     }
01974 
01975   switch(element_animation->GetState())
01976     {
01977       case UI_ELEMENT_ANIMATION_STATE_UNKNOWN   : break;
01978 
01979       case UI_ELEMENT_ANIMATION_STATE_PLAY      : if(element_animation->GetXTimerPlay()->GetMeasureMilliSeconds() >= element_animation->GetMilliSecondsDelay())
01980                                                     {
01981                                                       element_animation->IndexImage_Add();
01982                                                       element_animation->GetXTimerPlay()->Reset();  
01983                                                       redraw = true;                                                          
01984                                                     }
01985                                                   
01986                                                   break;
01987 
01988       case UI_ELEMENT_ANIMATION_STATE_STOP      : element_animation->IndexImage_Reset();                                                  
01989                                                   redraw = true;
01990                                                   break;
01991 
01992 
01993       case UI_ELEMENT_ANIMATION_STATE_PAUSE     : break;
01994     }
01995 
01996   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01997   
01998   if(redraw) Elements_SetToRedraw(element_animation);  
01999 
02000   return true;
02001 }
