02003 
02016 bool UI_SKINCANVAS::Draw_Option(UI_ELEMENT* element)  
02017 {
02018   if(!element) return false;
02019  
02020   UI_ELEMENT_OPTION*    element_option    = (UI_ELEMENT_OPTION*)element;
02021   UI_ELEMENT_ANIMATION* element_animation = element_option->Get_UIAnimation();
02022   UI_ELEMENT_TEXT*      element_text      = element_option->Get_UIText();
02023   double                x_position        = 0.0f;
02024   double                y_position        = 0.0f;
02025   GRPCANVAS*            canvas            = GetCanvas();
02026   XRECT                 clip_rect;              
02027 
02028   if(!canvas) return false;
02029 
02030   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_PRESELECT_MAXEDGE);
02031 
02032   if(element->MustReDraw()) 
02033     {
02034       bool isselected  = false;
02035       bool ispreselect = false;
02036 
02037       if(element_option->GetFather()) 
02038         {
02039           if(element_option->GetFather()->GetType() != UI_ELEMENT_TYPE_MULTIOPTION)
02040             {
02041               ispreselect = element_option->IsPreSelect();
02042               if(!ispreselect) ispreselect = element_option->GetFather()->IsPreSelect();    
02043             }
02044 
02045           if(element_option->GetFather()->GetType() != UI_ELEMENT_TYPE_MULTIOPTION)
02046             {
02047               isselected = element_option->IsSelected();
02048               if(!isselected) isselected = element_option->GetFather()->IsSelected();    
02049             }
02050   
02051         } 
02052        else 
02053         {
02054           isselected = element_option->IsSelected();
02055           ispreselect = element_option->IsPreSelect();
02056         }
02057 
02058       if(element->IsBlinking() && !element->GetStateBlink()) ispreselect = false;
02059 
02060       DrawBackgroundColor(element_option, canvas, x_position, y_position);
02061       
02062       if(isselected)
02063         { 
02064           GRP2DCOLOR_RGBA8  colorred(255, 0, 0);     
02065           GRP2DCOLOR_RGBA8  colorhover(0x32, 0x33, 0x36, 150);
02066   
02067           canvas->SetLineWidth(0.0f);
02068           canvas->SetLineColor(&colorred);
02069           canvas->SetFillColor(&colorhover);
02070       
02071           canvas->Rectangle(x_position  - UI_SKINCANVAS_PRESELECT_MAXEDGE                                      , 
02072                             y_position  + UI_SKINCANVAS_PRESELECT_MAXEDGE                                      ,
02073                             x_position  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_PRESELECT_MAXEDGE , 
02074                             y_position  - element->GetBoundaryLine()->height - UI_SKINCANVAS_PRESELECT_MAXEDGE , true);      
02075         }
02076        else
02077         {
02078           if(ispreselect)
02079             { 
02080               
02081               GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255);
02082               GRP2DCOLOR_RGBA8  colorhover(0x32, 0x33, 0x36, 150);
02083   
02084               canvas->SetLineWidth(0.0f);
02085               canvas->SetLineColor(&colorwhite);
02086               canvas->SetFillColor(&colorhover);
02087       
02088               canvas->Rectangle(x_position  - UI_SKINCANVAS_PRESELECT_MAXEDGE                                      , 
02089                                 y_position  + UI_SKINCANVAS_PRESELECT_MAXEDGE                                      ,
02090                                 x_position  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_PRESELECT_MAXEDGE , 
02091                                 y_position  - element->GetBoundaryLine()->height - UI_SKINCANVAS_PRESELECT_MAXEDGE , true);      
02092             }
02093          }
02094 
02095       if(element_animation)  Draw(element_animation);    
02096       if(element_text)       Draw(element_text);  
02097     }
02098 
02099   
02100   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02101 
02102   if(element_animation )
02103     {
02104       if(element_animation->GetState() == UI_ELEMENT_ANIMATION_STATE_PLAY)
02105         { 
02106           Elements_SetToRedraw(element_option);  
02107         }
02108     }
02109 
02110   return true;
02111 }
02112 
02113 
02126 bool UI_SKINCANVAS::Draw_MultiOption(UI_ELEMENT* element)
02127 {    
02128   if(!screen)  return false;
02129   if(!element) return false;
02130 
02131   UI_ELEMENT_MULTIOPTION* element_multioption  = (UI_ELEMENT_MULTIOPTION*)element;
02132   double                  x_position           = 0.0f;
02133   double                  y_position           = 0.0f;
02134   GRPCANVAS*              canvas               = GetCanvas();
02135   XRECT                   clip_rect;  
02136   
02137   if(!canvas) return false;
02138   
02139   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
02140 
02141   UI_PROPERTY_SELECTABLE_STATE actual_state = UI_PROPERTY_SELECTABLE_STATE_DEACTIVE;
02142   
02143   if(element_multioption->IsSelected()) 
02144     { 
02145       actual_state = UI_PROPERTY_SELECTABLE_STATE_SELECTED;
02146     }
02147    else 
02148     {
02149       if(element_multioption->IsPreSelect()) 
02150         {
02151           actual_state = UI_PROPERTY_SELECTABLE_STATE_PRESELECT;        
02152         }
02153        else 
02154         {
02155           if(element_multioption->IsActive()) 
02156             { 
02157               actual_state = UI_PROPERTY_SELECTABLE_STATE_ACTIVE;              
02158             }
02159         }
02160     }
02161 
02162   //if(element->MustReDraw()) 
02163     {
02164       DrawBackgroundColor(element_multioption, canvas, x_position, y_position);
02165  
02166       for(XDWORD c=0; c<element_multioption->GetComposeElements()->GetSize(); c++)
02167         {
02168           UI_ELEMENT_OPTION* element_option = (UI_ELEMENT_OPTION*)element_multioption->GetComposeElements()->Get(c);
02169           if(element_option) 
02170             {
02171               if(element_option->GetSelectableState() == actual_state) 
02172                 {
02173                   Draw(element_option);              
02174                   break;              
02175                 }       
02176             }            
02177         }
02178     }
02179       
02180   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02181 
02182   return true;
02183 }
