02229 
02242 bool UI_SKINCANVAS::Draw_EditText(UI_ELEMENT* element)  
02243 {    
02244   if(!element) return false;  
02245 
02246   UI_ELEMENT_EDITTEXT*  element_edittext  = (UI_ELEMENT_EDITTEXT*)element;
02247   double                x_position        = 0.0f;
02248   double                y_position        = 0.0f;
02249   GRPCANVAS*            canvas            = GetCanvas();    
02250   static bool           visible           = false;
02251   XRECT                 clip_rect;                       
02252   
02253   if(!canvas) return false;
02254 
02255   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_EDIT_MAXEDGE);
02256   
02257   GRP2DCOLOR_RGBA8  color(element->GetColor()->GetRed(),
02258                           element->GetColor()->GetGreen(),
02259                           element->GetColor()->GetBlue(),
02260                           element->GetColor()->GetAlpha());
02261 
02262   GRP2DCOLOR_RGBA8  bkgcolor(element->GetBackgroundColor()->GetRed(),
02263                              element->GetBackgroundColor()->GetGreen(),
02264                              element->GetBackgroundColor()->GetBlue(),
02265                              element_edittext->IsPreSelect()?element->GetBackgroundColor()->GetAlpha()/2:element->GetBackgroundColor()->GetAlpha());
02266 
02267   GRP2DCOLOR_RGBA8  linecolor;
02268 
02269   GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255, 255);
02270  
02271   if(element->MustReDraw()) 
02272     {
02273       canvas->SetLineColor(&linecolor);
02274       canvas->SetFillColor(&bkgcolor);
02275 
02276       if(element->GetRoundRect())
02277         {
02278           canvas->RoundRect(element->GetXPosition()  - UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02279                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,       
02280                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02281                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , element->GetRoundRect(), true);        
02282         }
02283        else
02284         {
02285           canvas->Rectangle(element->GetXPosition()  - UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02286                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,       
02287                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02288                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , true);
02289         }
02290    
02291                                    
02292       canvas->Vectorfont_GetConfig()->SetColor(&color);
02293       canvas->Vectorfont_GetConfig()->SetSize(element_edittext->GetSizeFont());  
02294       canvas->VectorFont_Printf(element->GetXPosition(), element->GetYPosition(), element_edittext->GetText()->Get());
02295 
02296       if(element_edittext->Cursor_IsVisible())
02297         {
02298           XDWORD    cursor_xpos = 0;
02299           XSTRING   cursor_string;
02300 
02301           element_edittext->GetText()->Copy(0, element_edittext->Cursor_GetPosition(), cursor_string);
02302 
02303           cursor_xpos = (XDWORD)GetWithString(cursor_string.Get(), element_edittext->GetSizeFont());
02304        
02305           if(element_edittext->Cursor_GetXTimerBlink()->GetMeasureMilliSeconds() >= element_edittext->Cursor_GetTimeBlink())
02306             {
02307               element_edittext->Cursor_ChangeStateBlink();   
02308               element_edittext->Cursor_GetXTimerBlink()->Reset();
02309             } 
02310  
02311           if(element_edittext->Cursor_GetStateBlink())
02312             {
02313               int cursor_size = 3;
02314 
02315               canvas->SetLineColor(&colorwhite);
02316               canvas->SetFillColor(&colorwhite);
02317 
02318               canvas->Rectangle( x_position + cursor_xpos , 
02319                                  y_position + UI_SKINCANVAS_EDIT_MAXEDGE - 4  ,
02320                                  x_position + cursor_xpos + cursor_size , 
02321                                  y_position - element_edittext->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE + 4 , true);                          
02322             }
02323         }
02324     }
02325 
02326   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02327 
02328   return true;
02329 }
