02331 
02344 bool UI_SKINCANVAS::Draw_Form(UI_ELEMENT* element)
02345 {    
02346   if(!screen)  return false;
02347   if(!element) return false;
02348 
02349   UI_ELEMENT_FORM*    element_form = (UI_ELEMENT_FORM*)element;
02350   double              x_position   = 0.0f;
02351   double              y_position   = 0.0f;
02352   GRPCANVAS*          canvas       = GetCanvas();
02353   XRECT               clip_rect;  
02354   
02355   if(!canvas) return false;
02356   
02357   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
02358 
02359   if(element->MustReDraw()) 
02360     {
02361       GRP2DCOLOR_RGBA8  color(element_form->GetColor()->GetRed(),
02362                               element_form->GetColor()->GetGreen(),
02363                               element_form->GetColor()->GetBlue(),
02364                               element_form->GetColor()->GetAlpha());
02365 
02366       GRP2DCOLOR_RGBA8  linecolor(element_form->GetLineColor()->GetRed(),
02367                                   element_form->GetLineColor()->GetGreen(),
02368                                   element_form->GetLineColor()->GetBlue(),
02369                                   element_form->GetLineColor()->GetAlpha());
02370 
02371       canvas->SetLineWidth(0.5f);
02372       canvas->SetLineColor(&linecolor);
02373       canvas->SetFillColor(&color);
02374 
02375       if(element_form->GetRoundRect())
02376         {
02377           canvas->RoundRect(element_form->GetVisibleRect()->x, 
02378                             element_form->GetVisibleRect()->y,
02379                             element_form->GetVisibleRect()->x + element_form->GetVisibleRect()->width   , 
02380                             element_form->GetVisibleRect()->y - element_form->GetVisibleRect()->height  , element_form->GetRoundRect(), true);
02381         }
02382        else
02383         {
02384           canvas->Rectangle(element_form->GetVisibleRect()->x, 
02385                             element_form->GetVisibleRect()->y,
02386                             element_form->GetVisibleRect()->x + element_form->GetVisibleRect()->width   , 
02387                             element_form->GetVisibleRect()->y - element_form->GetVisibleRect()->height  ,  true);
02388         }
02389     }
02390 
02391   for(XDWORD c=0; c<element_form->GetComposeElements()->GetSize(); c++)
02392     {
02393       UI_ELEMENT* subelement = (UI_ELEMENT*)element->GetComposeElements()->Get(c);
02394       if(subelement) 
02395         {
02396           Draw(subelement);
02397         }  
02398     }
02399   
02400   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02401 
02402   return true;
02403 }
