02443   static bool           visible         = false;
02444   XRECT                 clip_rect;                       
02445   
02446   if(!canvas) return false;
02447 
02448   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_EDIT_MAXEDGE);
02449 
02450   if(element->MustReDraw()) 
02451     {
02452       GRP2DCOLOR_RGBA8  color(element->GetColor()->GetRed(),
02453                               element->GetColor()->GetGreen(),
02454                               element->GetColor()->GetBlue(),
02455                               element->GetColor()->GetAlpha());
02456 
